import React, { useEffect, useState, useCallback } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { AiFillEye, AiOutlineMessage, AiTwotoneEdit, AiFillDelete } from 'react-icons/ai'
import Moment from 'react-moment'
import { Link, useNavigate, useParams } from 'react-router-dom'
import { toast } from 'react-toastify'
import axios from '../utils/axios'
import { removePost } from '../redux/features/post/postSlice'
import { createComment, getPostComments } from '../redux/features/comment/commentSlice'
import { CommentItem } from '../components/CommentItem'

export const PostPage = () => {
    const [post, setPost] = useState(null)
    const [comment, setComment] = useState('')

    const { user } = useSelector((state) => state.auth)
    const { comments } = useSelector((state) => state.comment)
    const navigate = useNavigate()
    const params = useParams()
    const dispatch = useDispatch()

    const removePostHandler = () => {
        try {
            dispatch(removePost(params.id))
            toast('Пост был удален')
            navigate('/posts')
        } catch (error) {
            console.log(error)
        }
    }

    const handleSubmit = () => {
        try {
            const postId = params.id
            dispatch(createComment({ postId, comment }))
            setComment('')
        } catch (error) {
            console.log(error)
        }
    }

    const fetchComments = useCallback(async () => {
        try {
            dispatch(getPostComments(params.id))
        } catch (error) {
            console.log(error)
        }
    }, [params.id, dispatch])

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setPost(data)
    }, [params.id])

    useEffect(() => {
        fetchPost()
    }, [fetchPost])

    useEffect(() => {
        fetchComments()
    }, [fetchComments])

    if (!post) {
        return (
            <div className='text-xl text-center text-white py-10'>
                Загрузка...
            </div>
        )
    }

    return (
        <div className='max-w-7xl mx-auto px-4'>
            <button className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4 mb-6'>
                <Link className='flex' to={'/posts'}>
                    Назад
                </Link>
            </button>

            <div className='flex gap-10 py-8'>
                {/* Post Section */}
                <div className='w-2/3'>
                    <div className='flex flex-col'>
                        <div className={post?.imgUrl ? 'flex rounded-sm h-80' : 'flex rounded-sm'}>
                            {post?.imgUrl && (
                                <img
                                    src={`http://localhost:3002/${post.imgUrl}`}
                                    alt='img'
                                    className='object-cover w-full h-full rounded-sm'
                                />
                            )}
                        </div>

                        <div className='flex justify-between items-center pt-4'>
                            <div className='text-xs text-gray-500'>{post.username}</div>
                            <div className='text-xs text-gray-500'>
                                <Moment date={post.createdAt} format='D MMM YYYY' />
                            </div>
                        </div>

                        <div className='text-2xl font-semibold text-white mt-2'>{post.title}</div>
                        <p className='text-white opacity-70 text-sm pt-4'>{post.text}</p>

                        <div className='flex gap-4 items-center mt-4 justify-between'>
                            <div className='flex gap-4'>
                                <button className='flex items-center gap-1 text-xs text-gray-300'>
                                    <AiFillEye />
                                    <span>{post.views}</span>
                                </button>
                                <button className='flex items-center gap-1 text-xs text-gray-300'>
                                    <AiOutlineMessage />
                                    <span>{post.comments?.length || 0}</span>
                                </button>
                            </div>

                            {user?._id === post.author && (
                                <div className='flex gap-4'>
                                    <Link to={`/${params.id}/edit`} className='text-gray-300'>
                                        <AiTwotoneEdit />
                                    </Link>
                                    <button
                                        onClick={removePostHandler}
                                        className='text-gray-300'
                                    >
                                        <AiFillDelete />
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Comment Section */}
                <div className='w-1/3 p-6 bg-gray-800 rounded-lg'>
                    <form onSubmit={(e) => e.preventDefault()} className='flex flex-col gap-4'>
                        <input
                            type='text'
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                            placeholder='Оставьте комментарий'
                            className='text-black w-full rounded-sm bg-gray-300 border p-2 text-sm outline-none placeholder:text-gray-700'
                        />
                        <button
                            type='submit'
                            onClick={handleSubmit}
                            className='bg-gray-600 text-xs text-white rounded-sm py-2 px-4 w-full'
                            disabled={!user}
                        >
                            Отправить
                        </button>
                    </form>

                    {/* Comments List */}
                    <div className='mt-6'>
                        {comments?.map((cmt) => (
                            <CommentItem key={cmt._id} cmt={cmt} />
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}
