import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from '../../../utils/axios'

const initialState = {
    posts: [],
    favorites: [],
    popularPosts: [],
    loading: false,
    availableTags: ['Anime', 'Movie', 'Lifestyle', 'Gaming', 'Education', 'Food', 'Travel'],
}

export const createPost = createAsyncThunk(
    'post/createPost',
    async (params) => {
        try {
            const { data } = await axios.post('/posts', params)
            return data
        } catch (error) {
            console.log(error)
        }
    },
)


export const getAllPosts = createAsyncThunk('post/getAllPosts', async ({ searchQuery }) => {
    try {
        const { data } = await axios.get(`/posts?searchQuery=${searchQuery}`);  
        return data;
    } catch (error) {
        console.log(error);
    }
});


export const removePost = createAsyncThunk('post/removePost', async (id) => {
    try {
        const { data } = await axios.delete(`/posts/${id}`, id)
        return data
    } catch (error) {
        console.log(error)
    }
}) 

export const updatePost = createAsyncThunk(
    'post/updatePost',
    async (updatedPost) => {
        try {
            const { data } = await axios.put(
                `/posts/${updatedPost.id}`,
                updatedPost,
            )
            return data
        } catch (error) {
            console.log(error)
        }
    },
)

export const toggleLike = createAsyncThunk(
    'post/toggleLike',
    async ({ postId, userId }) => {
        try {
            const { data } = await axios.put(`/posts/like/${postId}`)
            return { postId, userId, likes: data.post.likes }
        } catch (error) {
            console.log(error)
        }
    },
)

export const selectAvailableTags = (state) => state.post.availableTags;

export const getPostsByTag = createAsyncThunk(
    'post/getPostsByTag',
    async (tag) => {
        try {
            const { data } = await axios.get(`/posts/tag/${tag}`)
            return data
        } catch (error) {
            console.log(error)
        }
    },
)

export const toggleFavorite = createAsyncThunk(
    'post/toggleFavorite',
    async ({ postId, userId }) => {
        try {
            if (!userId) {
                throw new Error('User is not logged in');
            }
            const { data } = await axios.put(`/posts/favorite/${postId}`);

            return { postId, userId, favorites: data?.post?.favorites || [] };
        } catch (error) {
            console.log(error);
            throw error;
        }
    },
);




export const postSlice = createSlice({
    name: 'post',
    initialState,
    reducers: {},
    extraReducers: {
        // Создание поста
        [createPost.pending]: (state) => {
            state.loading = true
        },
        [createPost.fulfilled]: (state, action) => {
            state.loading = false
            state.posts.push(action.payload)
        },
        [createPost.rejected]: (state) => {
            state.loading = false
        },
        // Получаение всех постов
        [getAllPosts.pending]: (state) => {
            state.loading = true
        },
        [getAllPosts.fulfilled]: (state, action) => {
            state.loading = false;
            state.posts = action.payload?.posts || [];  // Ensure it's an array even if payload is undefined
            state.popularPosts = action.payload?.popularPosts || [];
        },

        [getAllPosts.rejected]: (state) => {
            state.loading = false
        },
        // Удаление поста
        [removePost.pending]: (state) => {
            state.loading = true
        },
        [removePost.fulfilled]: (state, action) => {
            state.loading = false
            state.posts = state.posts.filter(
                (post) => post._id !== action.payload._id,
            )
        },
        [removePost.rejected]: (state) => {
            state.loading = false
        },
        // Обновление поста
        [updatePost.pending]: (state) => {
            state.loading = true
        },
        [updatePost.fulfilled]: (state, action) => {
            state.loading = false
            const index = state.posts.findIndex(
                (post) => post._id === action.payload._id,
            )
            state.posts[index] = action.payload
        },
        [updatePost.rejected]: (state) => {
            state.loading = false
        },
        [toggleLike.fulfilled]: (state, action) => {
            const post = state.posts.find((post) => post._id === action.payload.postId)
            if (post) {
                post.likes = action.payload.likes
            }
        },
        [getPostsByTag.pending]: (state) => {
            state.loading = true
        },
        [getPostsByTag.fulfilled]: (state, action) => {
            state.loading = false
            state.posts = action.payload
        },
        [getPostsByTag.rejected]: (state) => {
            state.loading = false
        },
        [toggleFavorite.fulfilled]: (state, action) => {
            const post = state.posts.find((post) => post._id === action.payload.postId);
            if (post) {
                post.favorites = action.payload.post.favorites || [];
            }
        }
    },
})

export default postSlice.reducer
