import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';

export const PostsPage = () => {
  const [posts, setPosts] = useState([]);
  const { user, token, favorites } = useSelector((state) => state.auth);  // Get the user's favorites from the Redux state
  const [newAvatar, setNewAvatar] = useState(null);
  const dispatch = useDispatch();

  const fetchMyPosts = async () => {
    try {
      const { data } = await axios.get('/posts/user/me');
      setPosts(data);
    } catch (error) {
      console.log(error);
    }
  };

  const avatarImg = "/img/User-avatar.png";

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewAvatar(file);
    }
  };

  const isVideo = (url) => {
    const videoExtensions = ['.mp4', '.webm', '.ogg'];
    return videoExtensions.some((ext) => url.endsWith(ext));
  };

  const handleAvatarSubmit = async () => {
    if (!newAvatar) {
      alert("Please select an avatar image.");
      return;
    }

    const formData = new FormData();
    formData.append('avatar', newAvatar);

    try {
      const { data } = await axios.post('http://localhost:3002/api/auth/upload-avatar', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${window.localStorage.getItem('token')}`,
        },
      });

      dispatch({ type: 'auth/updateAvatar', payload: data.imgUrl });
      alert('Avatar updated successfully!');
    } catch (error) {
      console.error('Error uploading avatar:', error);
      alert('Failed to upload avatar');
    }
  };

  useEffect(() => {
    fetchMyPosts();
  }, []);

  // Filter posts that the user has favorited
  const favoritePosts = posts.filter(post => favorites.includes(post._id));

  return (
    <div className='w-full max-w-4xl mx-auto py-12'>
      <div className="flex items-center mb-8 space-x-4">
        <img 
          src={user?.imgUrl ? `http://localhost:3002/uploads/${user.imgUrl}` : avatarImg} 
          alt="User Avatar"
          className="w-16 h-16 rounded-full object-cover"
        />
        <div>
          <h2 className="text-2xl font-semibold text-white">{user?.username || "User"}</h2>
          <p className="text-gray-300 text-sm">Welcome back!</p>
        </div>
        <input 
          type="file" 
          accept="image/*"
          onChange={handleAvatarChange} 
          className="hidden" 
          id="avatar-input" 
        />
        <label htmlFor="avatar-input" className="cursor-pointer text-blue-500">Change Avatar</label>
        <button onClick={handleAvatarSubmit} className="ml-4 text-white bg-blue-500 px-4 py-2 rounded">Save Avatar</button>
      </div>

      {/* Display only favorited posts */}
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
        {favoritePosts?.filter(post => post !== null && post.imgUrl).map((post, idx) => (
          <div key={idx} className='h-40 w-full'>
            {isVideo(post.imgUrl) ? (
              <Link to={`/${post._id}`}>
                <video
                  width="100%"
                  height="100%"
                  className='object-cover w-full h-full'
                  muted
                  loop
                >
                  <source src={`http://localhost:3002/${post.imgUrl}`} type={`video/${post.imgUrl.split('.').pop()}`} />
                  Ваш браузер не поддерживает видео.
                </video>
              </Link>
            ) : (
              <Link to={`/${post._id}`}>
                <img
                  src={`http://localhost:3002/${post.imgUrl}`}
                  alt='Post content'
                  className='object-cover w-full h-full'
                />
              </Link>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};
