import React, { useState, useRef, useEffect } from 'react';
import Moment from 'react-moment';
import { Link } from 'react-router-dom';
import { AiFillEye, AiOutlineMessage, AiFillHeart, AiOutlineHeart, AiOutlineShareAlt, AiTwotoneEdit, AiFillDelete } from 'react-icons/ai';
import { FaTelegramPlane, FaWhatsapp, FaLink, FaBookmark, FaRegBookmark, FaVolumeUp, FaVolumeMute } from 'react-icons/fa';
import { useDispatch, useSelector } from 'react-redux';
import { toggleLike } from '../redux/features/post/postSlice';
import { toggleFavorite } from '../redux/features/auth/authSlice';

export const PostItem = ({ post, onRemovePost }) => {
    const dispatch = useDispatch();
    const user = useSelector((state) => state.auth.user);
    const userId = user?._id;
    const [isLiked, setIsLiked] = useState(userId && post.likes.includes(userId));
    const [isFavorited, setIsFavorited] = useState(userId && user.favorites.includes(post._id));
    const [isShareOpen, setIsShareOpen] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isMuted, setIsMuted] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);  // State for menu visibility

    const videoRef = useRef(null);

    const handleLike = () => {
        if (userId) {
            dispatch(toggleLike({ postId: post._id, userId }));
            setIsLiked((prev) => !prev);
        }
    };

    const handleFavorite = () => {
        if (userId) {
            dispatch(toggleFavorite({ postId: post._id, userId })).then(() => {
                setIsFavorited((prev) => !prev);
            });
        }
    };

    const togglePlayPause = () => {
        if (isPlaying) {
            videoRef.current.pause();
        } else {
            videoRef.current.play();
        }
        setIsPlaying(!isPlaying);
    };

    const toggleMute = () => {
        setIsMuted((prev) => !prev);
        videoRef.current.muted = !isMuted; 
    };

    const isVideo = (url) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg'];
        return videoExtensions.some((ext) => url.endsWith(ext));
    };

    const toggleShareMenu = () => setIsShareOpen((prev) => !prev);

    const copyToClipboard = () => {
        const postUrl = `http://localhost:3000/${post._id}`;
        navigator.clipboard.writeText(postUrl)
            .then(() => alert('Ссылка скопирована!'))
            .catch(() => alert('Ошибка при копировании ссылки.'));
    };

    const toggleMenu = () => setIsMenuOpen((prev) => !prev);  // Toggle menu visibility

    useEffect(() => {
        if (isShareOpen) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'auto';
        }

        return () => { 
            document.body.style.overflow = 'auto';
        };
    }, [isShareOpen]);

    if (!post) {
        return (
            <div className='text-xl text-center text-white py-10'>
                Загрузка...
            </div>
        );
    }

    const postUrl = `http://localhost:3000/${post._id}`;
    const shareMessage = encodeURIComponent(`Смотри, что я нашел: ${post.title} - ${postUrl}`);

    return (
        <div className='group'>
            <div className='bg-white shadow-lg rounded-lg overflow-hidden hover:shadow-xl transition-shadow duration-300'>
                <div className='flex justify-between items-center bg-gray-100 px-4 py-2'>
                    <div className='flex items-center gap-2'>
                        <img
                            src={post?.authorAvatar ? `http://localhost:3002/${post.authorAvatar}` : 'img/User-avatar.png'} 
                            alt="Author avatar"
                            className="w-10 h-10 rounded-full object-cover"
                        />
                        <div className='text-sm font-semibold text-gray-800'>{post.username}</div>
                    </div>
                    <div className='text-xs text-gray-500'>
                        <Moment date={post.createdAt} format='D MMM YYYY' />
                    </div>
                    {/* Menu Button */}
                    {userId === post.author || user?.username === 'Admin' ? (
                        <div className="relative">
                            <button onClick={toggleMenu} className="text-gray-500 hover:text-gray-700">
                                ⋮
                            </button>
                            {isMenuOpen && (
                                <div className="absolute right-0 mt-2 bg-white shadow-lg rounded-lg w-40">
                                    <button 
                                        onClick={() => onRemovePost(post._id)}
                                        className="block text-gray-700 w-full text-left px-4 py-2 hover:bg-gray-100">
                                        Удалить
                                    </button>
                                    <Link
                                        to={`/${post._id}/edit`}
                                        className="block text-gray-700 w-full text-left px-4 py-2 hover:bg-gray-100"
                                    >
                                        Редактировать
                                    </Link>
                                </div>
                            )}
                        </div>
                    ) : null}
                </div>

                <div className={`${post.imgUrl && isVideo(post.imgUrl) ? 'h-auto' : 'h-80'} transition-all`}>
                    {post.imgUrl && isVideo(post.imgUrl) ? (
                        <div className="relative group">
                            <video
                                ref={videoRef}
                                width="100%"
                                height="100%"
                                muted={isMuted} 
                                className='object-cover w-full'
                            >
                                <source src={`http://localhost:3002/${post.imgUrl}`} type="video/mp4" />
                                Ваш браузер не поддерживает видео.
                            </video>
                            <div className="absolute inset-0 flex justify-center items-center bg-black bg-opacity-30 group-hover:block hidden">
                                <button onClick={togglePlayPause} className='text-white'>
                                    {isPlaying ? (
                                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-8 h-8">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7-7 7M5 5l7 7-7 7" />
                                        </svg>
                                    ) : (
                                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-8 h-8">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 3v18l15-9-15-9z" />
                                        </svg>
                                    )}
                                </button>
                            </div>
                            <button 
                                onClick={toggleMute} 
                                className="absolute bottom-4 right-4 p-2 bg-black bg-opacity-50 rounded-full text-white"
                            >
                                {isMuted ? <FaVolumeMute className="w-6 h-6" /> : <FaVolumeUp className="w-6 h-6" />}
                            </button>
                        </div>
                    ) : (
                        <img
                            src={`http://localhost:3002/${post.imgUrl}`}
                            alt='Post content'
                            className='object-cover w-full h-full'
                        />
                    )}
                </div>

                <div className='p-4'>
                    {post.tags && post.tags.length > 0 && (
                        <div className='mb-2 flex flex-wrap gap-2'>
                            {post.tags.map((tag, index) => (
                                <span key={index} className='px-2 py-1 bg-gray-200 rounded-full text-sm text-gray-800'>
                                    #{tag}
                                </span>
                            ))}
                        </div>
                    )}

                    <div className='mt-2'>
                        <div className='text-lg font-semibold text-gray-800'>{post.title}</div>
                        <p className='text-sm text-gray-600 mt-2 line-clamp-4'>{post.text}</p>
                    </div>

                    <div className='flex gap-4 items-center mt-4 text-base text-gray-500'>
                        <button className='flex items-center gap-1' onClick={handleLike}>
                            {isLiked ? (
                                <AiFillHeart className='text-red-500' />
                            ) : (
                                <AiOutlineHeart />
                            )}
                            <span>{post.likes.length}</span>
                        </button>
                        <button className='flex items-center gap-1'>
                            <AiFillEye />
                            <span>{post.views}</span>
                        </button>
                        <Link to={`/${post._id}`} className='group flex items-center gap-1'>
                            <AiOutlineMessage />
                            <span>{post.comments?.length || 0}</span>
                        </Link>
                        <div className="ml-auto flex gap-4 items-center">
                            <button className='flex items-center gap-1' onClick={handleFavorite}>
                                {isFavorited ? (
                                    <FaBookmark className='text-blue-500' />
                                ) : (
                                    <FaRegBookmark className='text-gray-500' />
                                )}
                            </button>

                            <div>
                                <button
                                    className='flex items-center gap-1'
                                    onClick={toggleShareMenu}
                                >
                                    <AiOutlineShareAlt />
                                </button>
                            </div>
                        </div>
                        {isShareOpen && (
                            <div className='fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50'>
                                <div className='bg-white border shadow-xl rounded-lg p-4 w-80'>
                                    <div className='flex items-center justify-between mb-3'>
                                        <div className='text-lg font-semibold text-gray-800'>Поделиться</div>
                                        <button onClick={toggleShareMenu} className='text-gray-500'>
                                            X
                                        </button>
                                    </div>
                                    <a
                                        href={`https://t.me/share/url?url=${encodeURIComponent(postUrl)}&text=${encodeURIComponent(shareMessage)}`}
                                        target='_blank'
                                        rel='noopener noreferrer'
                                        className='flex items-center gap-2 text-blue-500 hover:text-blue-700 mb-2'
                                    >
                                        <FaTelegramPlane />
                                        Telegram
                                    </a>
                                    <a
                                        href={`https://wa.me/?text=${encodeURIComponent(shareMessage)}`}
                                        target='_blank'
                                        rel='noopener noreferrer'
                                        className='flex items-center gap-2 text-green-500 hover:text-green-700 mb-2'
                                    >
                                        <FaWhatsapp />
                                        WhatsApp
                                    </a>

                                    <div className='flex items-center gap-2 text-gray-500'>
                                        <FaLink />
                                        <button onClick={copyToClipboard} className='text-sm hover:text-gray-700'>
                                            Копировать ссылку
                                        </button>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};
