import React, { useEffect, useState, useCallback } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams, Link } from 'react-router-dom'
import { toast } from 'react-toastify'
import { AiTwotoneEdit, AiFillDelete } from 'react-icons/ai'
import { removePost } from '../redux/features/post/postSlice'
import { createComment, getPostComments } from '../redux/features/comment/commentSlice'
import { CommentItem } from '../components/CommentItem'
import { PostItem } from '../components/PostItem'

export const PostPage = () => {
    const [comment, setComment] = useState('')
    const { user } = useSelector((state) => state.auth)
    const { comments } = useSelector((state) => state.comment)
    const navigate = useNavigate()
    const params = useParams()
    const dispatch = useDispatch()
    const userId = user?._id  // Проверяем, существует ли user и его _id

    const removePostHandler = () => {
        try {
            dispatch(removePost(params.id))
            toast('Пост был удален')
            navigate('/')
        } catch (error) {
            console.log(error)
        }
    }

    const handleSubmit = () => {
        try {
            const postId = params.id
            dispatch(createComment({ postId, comment }))
            setComment('')
        } catch (error) {
            console.log(error)
        }
    }

    const fetchComments = useCallback(async () => {
        try {
            dispatch(getPostComments(params.id))
        } catch (error) {
            console.log(error)
        }
    }, [params.id, dispatch])

    useEffect(() => {
        fetchComments()
    }, [fetchComments])

    return (
        <div className='max-w-screen-xl mx-auto p-4'>
            <button className='bg-gray-700 text-xs text-white rounded-sm py-2 px-4 mb-6 hover:bg-gray-600 transition'>
                <Link to='/' className='flex'>
                    Назад
                </Link>
            </button>

            <div className='flex flex-col gap-12'>
                {/* Edit and Delete buttons block */}
                {(user?._id === post?.author || user?.username === 'Admin') && (
                    <div className='flex justify-end gap-4 mb-4'>
                        <Link
                            to={`/${params.id}/edit`}
                            className='flex items-center text-gray-600 hover:text-gray-800'
                        >
                            <AiTwotoneEdit />
                            Редактировать
                        </Link>
                        <button
                            onClick={removePostHandler}
                            className='flex items-center text-gray-600 hover:text-gray-800'
                        >
                            <AiFillDelete />
                            Удалить
                        </button>
                    </div>
                )}

                {/* PostItem component */}
                <PostItem postId={params.id} />

                {/* Comment Section */}
                <div className='w-full bg-gray-800 rounded-lg p-6 mx-auto'>
                    <form
                        className='flex gap-4 mb-4'
                        onSubmit={(e) => e.preventDefault()}
                    >
                        <input
                            type='text'
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                            placeholder='Напишите комментарий...'
                            className='w-full rounded-sm bg-gray-600 border p-3 text-xs text-white placeholder:text-gray-400 focus:outline-none'
                        />
                        <button
                            type='submit'
                            onClick={handleSubmit}
                            className='bg-blue-600 text-white rounded-sm py-2 px-4 text-xs hover:bg-blue-700 transition'
                            disabled={!user}
                        >
                            Отправить
                        </button>
                    </form>

                    <div className='space-y-4'>
                        {comments?.map((cmt) => (
                            <CommentItem key={cmt._id} cmt={cmt} />
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}
