import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getFavorites } from '../redux/features/auth/authSlice';
import { FaHandPointer } from 'react-icons/fa';

export const PostsPage = () => {
    const [posts, setPosts] = useState([]);

    const { user, token, favorites } = useSelector((state) => state.auth);
    const dispatch = useDispatch();

    const fetchMyPosts = async () => {
        try {
            const { data } = await axios.get('/posts/user/me');
            setPosts(data);
        } catch (error) {
            console.log(error);
        }
    };

    const avatarImg = "/img/User-avatar.png";

    const isVideo = (url) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg'];
        return videoExtensions.some((ext) => url.endsWith(ext));
    };

    useEffect(() => {
        fetchMyPosts();
    }, [dispatch]);

    return (
        <div className='w-full max-w-6xl mx-auto py-12'>
            <div className="flex items-center mb-8 space-x-4">
                <Link to="/update_user">
                    <img
                        src={user?.imgUrl ? `http://localhost:3002/${user.imgUrl}` : avatarImg}
                        alt="User Avatar"
                        className="w-20 h-20 rounded-full object-cover border-2 border-gray-700 cursor-pointer opacity-90 hover:opacity-70 transition-all"
                    />
                    <FaHandPointer className="absolute inset-0 m-auto text-white opacity-0 group-hover:opacity-100 transition-all duration-300" size={30} />
                </Link>
                <div>
                    <h2 className="text-3xl font-semibold text-white">{user?.username || "User"}</h2>
                    <p className="text-gray-300 text-sm">Welcome back!</p>
                </div>
            </div>

            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 mb-12">
                {posts?.filter(post => post !== null && post.imgUrl).map((post, idx) => (
                    <div key={idx} className='relative h-48 w-full rounded-lg overflow-hidden border border-solid border-white shadow-lg'>
                        {isVideo(post.imgUrl) ? (
                            <Link to={`/${post._id}`}>
                                <video
                                    width="100%"
                                    height="100%"
                                    className='object-cover w-full h-full'
                                    muted
                                    loop
                                >
                                    <source src={`http://localhost:3002/${post.imgUrl}`} type={`video/${post.imgUrl.split('.').pop()}`} />
                                    Ваш браузер не поддерживает видео.
                                </video>
                            </Link>
                        ) : (
                            <Link to={`/${post._id}`}>
                                <img
                                    src={`http://localhost:3002/${post.imgUrl}`}
                                    alt='Post content'
                                    className='object-cover w-full h-full'
                                />
                            </Link>
                        )}
                    </div>
                ))}
            </div>

            <div className='max-w-screen-xl mx-auto p-6 rounded-lg'>
                <h2 className='text-2xl font-semibold text-white mb-4'>Избранные Посты</h2>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6">
                    {Array.isArray(favorites) && favorites.length > 0 ? (
                        favorites.map((favorite) => (
                            <div key={favorite._id} className='relative h-48 w-full rounded-lg overflow-hidden border border-solid border-white shadow-lg'>
                                {favorite.imgUrl && isVideo(favorite.imgUrl) ? (
                                    <Link to={`/${favorite._id}`}>
                                        <video
                                            width="100%"
                                            height="100%"
                                            className='object-cover w-full h-full'
                                            muted
                                            loop
                                        >
                                            <source src={`http://localhost:3002/${favorite.imgUrl}`} type={`video/${favorite.imgUrl.split('.').pop()}`} />
                                            Ваш браузер не поддерживает видео.
                                        </video>
                                    </Link>
                                ) : (
                                    <Link to={`/${favorite._id}`}>
                                        <img
                                            src={`http://localhost:3002/${favorite.imgUrl}`}
                                            alt='Post content'
                                            className='object-cover w-full h-full'
                                        />
                                    </Link>
                                )}
                            </div>
                        ))
                    ) : (
                        <p>No favorites found</p>
                    )}
                </div>
            </div>
        </div>
    );
};
