import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getFavorites } from '../redux/features/auth/authSlice';

export const PostsPage = () => {
    const [posts, setPosts] = useState([]);
    const { user, token, favorites } = useSelector((state) => state.auth);
    const [newAvatar, setNewAvatar] = useState(null);
    const dispatch = useDispatch();

    const fetchMyPosts = async () => {
        try {
            const { data } = await axios.get('/posts/user/me');
            setPosts(data);
        } catch (error) {
            console.log(error);
        }
    };

    const avatarImg = "/img/User-avatar.png";

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setNewAvatar(file);
        }
    };

    const isVideo = (url) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg'];
        return videoExtensions.some((ext) => url.endsWith(ext));
    };

    const handleAvatarSubmit = async () => {
        if (!newAvatar) {
            alert("Please select an avatar image.");
            return;
        }

        const formData = new FormData();
        formData.append('avatar', newAvatar);

        try {
            const { data } = await axios.post('http://localhost:3002/api/auth/upload-avatar', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${window.localStorage.getItem('token')}`,
                },
            });

            dispatch({ type: 'auth/updateAvatar', payload: data.imgUrl });
            alert('Avatar updated successfully!');
        } catch (error) {
            console.error('Error uploading avatar:', error);
            alert('Failed to upload avatar');
        }
    };

    useEffect(() => {
        fetchMyPosts();
        dispatch(getFavorites()).then(() => {
            console.log('Favorites:', favorites);
        });
    }, [dispatch]);

    const renderPostContent = (post) => {
        return isVideo(post.imgUrl) ? (
            <video
                width="100%"
                height="100%"
                className='object-cover w-full h-full'
                muted
                loop
            >
                <source src={`http://localhost:3002/${post.imgUrl}`} type={`video/${post.imgUrl.split('.').pop()}`} />
                Ваш браузер не поддерживает видео.
            </video>
        ) : (
            <img
                src={`http://localhost:3002/${post.imgUrl}`}
                alt='Post content'
                className='object-cover w-full h-full'
            />
        );
    };

    return (
        <div className='w-full max-w-6xl mx-auto py-12'>
            <div className="flex items-center mb-8 space-x-4">
                <img 
                    src={user?.imgUrl ? `http://localhost:3002/uploads/${user.imgUrl}` : avatarImg} 
                    alt="User Avatar"
                    className="w-20 h-20 rounded-full object-cover border-2 border-gray-700"
                />
                <div>
                    <h2 className="text-3xl font-semibold text-white">{user?.username || "User"}</h2>
                    <p className="text-gray-300 text-sm">Welcome back!</p>
                </div>
                <input 
                    type="file" 
                    accept="image/*"
                    onChange={handleAvatarChange} 
                    className="hidden" 
                    id="avatar-input" 
                />
                <label htmlFor="avatar-input" className="cursor-pointer text-blue-500 hover:underline">Change Avatar</label>
                <button onClick={handleAvatarSubmit} className="ml-4 text-white bg-blue-500 px-4 py-2 rounded-lg hover:bg-blue-600 transition">Save Avatar</button>
            </div>

            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 mb-12">
                {posts?.filter(post => post !== null && post.imgUrl).map((post, idx) => (
                    <div key={idx} className='relative h-48 w-full rounded-lg overflow-hidden border border-solid border-white shadow-lg'>
                        <Link to={`/${post._id}`}>
                            {renderPostContent(post)}
                        </Link>
                    </div>
                ))}
            </div>

            <div className='max-w-screen-xl mx-auto p-6 bg-gray-800 rounded-lg'>
                <h2 className='text-2xl font-semibold text-white mb-4'>Избранные Посты</h2>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6">
                    {Array.isArray(favorites) && favorites.length > 0 ? (
                        favorites.map((favorite) => (
                            <div key={favorite._id} className='relative h-48 w-full rounded-lg overflow-hidden border border-solid border-white shadow-lg'>
                                <Link to={`/${favorite._id}`}>
                                    {renderPostContent(favorite)}
                                </Link>
                            </div>
                        ))
                    ) : (
                        <p>No favorites found</p>
                    )}
                </div>
            </div>
        </div>
    );
};
