import React, { useEffect, useState, useCallback } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams } from 'react-router-dom'
import { updatePost, selectAvailableTags, getAllPosts } from '../redux/features/post/postSlice'
import axios from '../utils/axios'

export const EditPostPage = () => {
    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    const [oldImage, setOldImage] = useState('')
    const [newImage, setNewImage] = useState('')
    const [selectedTags, setSelectedTags] = useState([])

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const params = useParams()
    const availableTags = useSelector(selectAvailableTags)

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setTitle(data.title)
        setText(data.text)
        setOldImage(data.imgUrl)
        setSelectedTags(data.tags ? data.tags.split(',') : [])
    }, [params.id])

    const handleTagChange = (tag) => {
        if (selectedTags.includes(tag)) {
            setSelectedTags(selectedTags.filter(t => t !== tag))
        } else {
            setSelectedTags([...selectedTags, tag])
        }
    }

    const submitHandler = () => {
        try {
            const updatedPost = new FormData()
            updatedPost.append('title', title)
            updatedPost.append('text', text)
            updatedPost.append('id', params.id)
            updatedPost.append('image', newImage)
            updatedPost.append('tags', selectedTags.join(',')) // Joining tags as a string
            dispatch(updatePost(updatedPost))
            const searchQuery = ''
            const page = 1
            dispatch(getAllPosts({ searchQuery, page }))
            navigate('/')
        } catch (error) {
            console.log(error)
        }
    }

    const clearFormHandler = () => {
        setTitle('')
        setText('')
        setSelectedTags([])
    }

    const isVideo = (filename) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg']
        return videoExtensions.some((ext) => filename.endsWith(ext))
    }

    useEffect(() => {
        fetchPost()
    }, [fetchPost])

    return (
        <form
            className="max-w-xl mx-auto py-8 px-10 bg-gray-800 rounded-lg shadow-lg"
            onSubmit={(e) => e.preventDefault()}
        >
            <h2 className="text-2xl text-white font-semibold text-center mb-6">Edit Post</h2>

            {/* File Upload Section */}
            <label className="block text-sm text-gray-300 font-medium mb-4">
                Attach a file:
                <div className="relative">
                    <input
                        type="file"
                        className="hidden"
                        onChange={(e) => {
                            setNewImage(e.target.files[0])
                            setOldImage('') // Clear old image when a new one is uploaded
                        }}
                    />
                    <div className="flex items-center justify-center py-6 bg-gray-700 border-2 border-dashed border-gray-500 rounded-lg cursor-pointer hover:bg-gray-600 transition">
                        <span className="text-gray-400 text-lg">Click to upload a file</span>
                    </div>
                </div>
            </label>

            <div className="flex justify-center py-2">
                {oldImage && (
                    isVideo(oldImage) ? (
                        <video
                            width="100%"
                            height="auto"
                            controls
                            className="w-32 h-32 object-cover rounded-lg shadow-md"
                        >
                            <source src={`http://localhost:3002/${oldImage}`} type="video/mp4" />
                            Your browser does not support video.
                        </video>
                    ) : (
                        <img
                            src={`http://localhost:3002/${oldImage}`}
                            alt={oldImage}
                            className="w-32 h-32 object-cover rounded-lg shadow-md"
                        />
                    )
                )}
                {newImage && (
                    isVideo(newImage.name) ? (
                        <video
                            width="100%"
                            height="auto"
                            controls
                            className="w-32 h-32 object-cover rounded-lg shadow-md"
                        >
                            <source src={URL.createObjectURL(newImage)} type="video/mp4" />
                            Your browser does not support video.
                        </video>
                    ) : (
                        <img
                            src={URL.createObjectURL(newImage)}
                            alt={newImage.name}
                            className="w-32 h-32 object-cover rounded-lg shadow-md"
                        />
                    )
                )}
            </div>

            {/* Title Section */}
            <label className="block text-sm text-gray-200 opacity-80 mt-4">
                Post Title:
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Enter the title"
                    className="mt-2 text-black w-full rounded-lg bg-gray-400 py-3 px-4 text-sm outline-none focus:ring-2 focus:ring-blue-500 placeholder:text-gray-700 transition"
                />
            </label>

            {/* Tags Section - Custom Dropdown with Checkboxes */}
            <label className="block text-sm text-gray-200 opacity-80 mt-4">
                Tags:
                <div className="flex flex-wrap gap-3 mt-2">
                    {availableTags.map((tag) => (
                        <button
                            key={tag}
                            type="button"
                            onClick={() => handleTagChange(tag)}
                            className={`px-4 py-2 text-sm font-medium rounded-lg transition-all ${selectedTags.includes(tag) ? 'bg-blue-500 text-white' : 'bg-gray-600 text-gray-200 hover:bg-blue-600'}`}
                        >
                            {tag}
                        </button>
                    ))}
                </div>
            </label>

            {/* Text Content Section */}
            <label className="block text-sm text-gray-200 mt-4">
                Content:
                <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Enter the content of the post"
                    className="mt-2 text-black w-full rounded-lg bg-gray-400 py-3 px-4 text-sm outline-none focus:ring-2 focus:ring-blue-500 placeholder:text-gray-700 transition"
                />
            </label>

            {/* Action Buttons */}
            <div className="flex justify-between items-center mt-6">
                <button
                    type="button"
                    onClick={clearFormHandler}
                    className="px-6 py-3 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition"
                >
                    Clear
                </button>
                <button
                    type="button"
                    onClick={submitHandler}
                    className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
                >
                    Update Post
                </button>
            </div>
        </form>
    )
}
