import React, { useEffect, useState, useCallback } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams } from 'react-router-dom'
import { updatePost, selectAvailableTags } from '../redux/features/post/postSlice'
import axios from '../utils/axios'

export const EditPostPage = () => {
    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    const [oldImage, setOldImage] = useState('')
    const [newImage, setNewImage] = useState('')
    const [selectedTags, setSelectedTags] = useState([])

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const params = useParams()
    const availableTags = useSelector(selectAvailableTags);

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setTitle(data.title)
        setText(data.text)
        setOldImage(data.imgUrl)
        setSelectedTags(data.tags ? data.tags.split(',') : []) // Assuming tags are stored as a comma-separated string
    }, [params.id])

    const handleTagChange = (tag) => {
        if (selectedTags.includes(tag)) {
            setSelectedTags(selectedTags.filter(t => t !== tag))
        } else {
            setSelectedTags([...selectedTags, tag])
        }
    }

    const submitHandler = () => {
        try {
            const updatedPost = new FormData()
            updatedPost.append('title', title)
            updatedPost.append('text', text)
            updatedPost.append('id', params.id)
            updatedPost.append('image', newImage)
            updatedPost.append('tags', selectedTags.join(',')) // Joining tags as a string
            dispatch(updatePost(updatedPost))
            navigate('/')
        } catch (error) {
            console.log(error)
        }
    }

    const clearFormHandler = () => {
        setTitle('')
        setText('')
        setSelectedTags([])
    }

    const isVideo = (filename) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg']
        return videoExtensions.some((ext) => filename.endsWith(ext))
    }

    useEffect(() => {
        fetchPost()
    }, [fetchPost])

    return (
        <form
            className='max-w-lg mx-auto py-12 px-8'
            onSubmit={(e) => e.preventDefault()}
        >
            <label className='block text-sm text-gray-300 font-semibold mb-2'>
                Прикрепить файл:
                <div className='relative' style={{ cursor: 'pointer' }}>
                    <input
                        type='file'
                        className='hidden'
                        onChange={(e) => {
                            setNewImage(e.target.files[0])
                            setOldImage('') // Clear old image when a new one is uploaded
                        }}
                    />
                    <div className='flex items-center justify-center py-8 bg-gray-700 border-2 border-dotted border-gray-500 rounded-lg'>
                        <span className='text-gray-400 text-lg'>
                            Нажмите, чтобы загрузить файл
                        </span>
                    </div>
                </div>
            </label>
            <div className='flex justify-center py-2'>
                {oldImage && (
                    isVideo(oldImage) ? (
                        <video
                            width="100%"
                            height="auto"
                            controls
                            className='w-32 h-32 object-cover rounded-lg shadow-md'
                        >
                            <source src={`http://localhost:3002/${oldImage}`} type="video/mp4" />
                            Ваш браузер не поддерживает видео.
                        </video>
                    ) : (
                        <img
                            src={`http://localhost:3002/${oldImage}`}
                            alt={oldImage}
                            className='w-32 h-32 object-cover rounded-lg shadow-md'
                        />
                    )
                )}
                {newImage && (
                    isVideo(newImage.name) ? (
                        <video
                            width="100%"
                            height="auto"
                            controls
                            className='w-32 h-32 object-cover rounded-lg shadow-md'
                        >
                            <source src={URL.createObjectURL(newImage)} type="video/mp4" />
                            Ваш браузер не поддерживает видео.
                        </video>
                    ) : (
                        <img
                            src={URL.createObjectURL(newImage)}
                            alt={newImage.name}
                            className='w-32 h-32 object-cover rounded-lg shadow-md'
                        />
                    )
                )}
            </div>

            <label className='block text-xs text-gray-200 opacity-80 mt-2'>
                Заголовок поста:
                <input
                    type='text'
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder='Введите заголовок'
                    className='mt-2 text-black w-full rounded-lg bg-gray-400 py-2 px-4 text-sm outline-none focus:ring-2 focus:ring-blue-500 placeholder:text-gray-700'
                />
            </label>

            <label className='block text-xs text-gray-200 opacity-80 mt-2'>
                Теги:
                <div className='flex flex-wrap gap-2 mt-2'>
                    {availableTags.map((tag) => (
                        <button
                            key={tag}
                            type='button'
                            onClick={() => handleTagChange(tag)}
                            className={`px-3 py-1 border rounded ${selectedTags.includes(tag) ? 'bg-blue-500 text-white' : 'bg-gray-300 text-black'}`}
                        >
                            {tag}
                        </button>
                    ))}
                </div>
            </label>

            <label className='block text-xs text-gray-200 opacity-80 mt-4'>
                Текст поста:
                <textarea
                    onChange={(e) => setText(e.target.value)}
                    value={text}
                    placeholder='Введите текст поста'
                    className='mt-2 text-black w-full rounded-lg bg-gray-400 py-2 px-4 text-sm outline-none focus:ring-2 focus:ring-blue-500 placeholder:text-gray-700 resize-none h-40'
                />
            </label>

            <div className='flex gap-8 items-center justify-center mt-6'>
                <button
                    onClick={submitHandler}
                    className='bg-blue-600 hover:bg-blue-700 text-white text-sm font-semibold py-2 px-6 rounded-lg transition duration-300'
                >
                    Обновить
                </button>

                <button
                    onClick={clearFormHandler}
                    className='bg-red-600 hover:bg-red-700 text-white text-sm font-semibold py-2 px-6 rounded-lg transition duration-300'
                >
                    Отменить
                </button>
            </div>
        </form>
    )
}
