import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';
import { FaHome, FaUser, FaBlog, FaCog, FaPlusCircle, FaSignOutAlt } from 'react-icons/fa';

export const DashBoard = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  // Функция для загрузки постов и пользователей
  useEffect(() => {
    setPosts([
      { id: 1, title: 'Первый пост', author: 'Автор 1', createdAt: '2024-11-01' },
      { id: 2, title: 'Второй пост', author: 'Автор 2', createdAt: '2024-11-02' },
    ]);
    setUsers([
      { id: 1, name: 'Пользователь 1', role: 'Админ' },
      { id: 2, name: 'Пользователь 2', role: 'Редактор' },
    ]);
  }, []);

  return (
    <div className="flex">
      {/* Sidebar */}
      <div className="w-64 bg-gray-200 p-4">
        <nav>
          <ul>
            <li>
              <NavLink to="/dashboard/posts" activeClassName="text-blue-500" className="flex items-center space-x-2 py-2">
                <FaBlog /> <span>Посты</span>
              </NavLink>
            </li>
            <li>
              <NavLink to="/dashboard/users" activeClassName="text-blue-500" className="flex items-center space-x-2 py-2">
                <FaUser /> <span>Пользователи</span>
              </NavLink>
            </li>
            <li>
              <NavLink to="/dashboard/settings" activeClassName="text-blue-500" className="flex items-center space-x-2 py-2">
                <FaCog /> <span>Настройки</span>
              </NavLink>
            </li>
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-4">
        
      </div>
    </div>
  );
};

export default DashBoard;
