import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from '../../../utils/axios';

const initialState = {
    user: null,
    token: null,
    isLoading: false,
    status: null,
    users: [], 
};

export const getAllUsers = createAsyncThunk(
    'auth/getAllUsers',
    async () => {
        try {
            const { data } = await axios.get('/users');
            return data;
        } catch (error) {
            console.log(error);
        }
    }
);



export const registerUser = createAsyncThunk(
    'auth/registerUser',
    async ({ username, email, password }) => {
        try {
            const { data } = await axios.post('/auth/register', {
                username,
                email, 
                password,
            });
            if (data.token) {
                window.localStorage.setItem('token', data.token);
            }
            return data;
        } catch (error) {
            console.log(error);
        }
    },
);

export const loginUser = createAsyncThunk(
    'auth/loginUser',
    async ({ identifier, password }) => {
        try {
            const { data } = await axios.post('/auth/login', {
                identifier, 
                password,
            });
            if (data.token) {
                window.localStorage.setItem('token', data.token);
            }
            return data;
        } catch (error) {
            console.log(error);
        }
    },
);


export const getMe = createAsyncThunk('auth/getMe', async () => {
    try {
        const { data } = await axios.get('/auth/me');
        return data;
    } catch (error) {
        console.log(error);
    }
});

export const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        logout: (state) => {
            state.user = null;
            state.token = null;
            state.isLoading = false;
            state.status = null;
        },
    },
    extraReducers: {
        // Get all users
        [getAllUsers.fulfilled]: (state, action) => {
            state.isLoading = false;
            state.users = action.payload; // Сохраняем пользователей в состояние
        },
        [getAllUsers.rejected]: (state, action) => {
            state.isLoading = false;
            state.status = action.error.message || 'Failed to fetch users';
        },
        [getAllUsers.pending]: (state) => {
            state.loading = true
        },
        [getAllPosts.fulfilled]: (state, action) => {
            state.loading = false
            state.posts = action.payload.posts
        },
        [getAllPosts.rejected]: (state) => {
            state.loading = false
        },
        // Register user
        [registerUser.pending]: (state) => {
            state.isLoading = true;
            state.status = null;
        },
        [registerUser.fulfilled]: (state, action) => {
            state.isLoading = false;
            state.status = action.payload.message;
            state.user = action.payload.user;
            state.token = action.payload.token;
        },
        [registerUser.rejected]: (state, action) => {
            state.status = action.payload?.message || 'Registration failed';
            state.isLoading = false;
        },
        // Login user
        [loginUser.pending]: (state) => {
            state.isLoading = true;
            state.status = null;
        },
        [loginUser.fulfilled]: (state, action) => {
            state.isLoading = false;
            state.status = action.payload.message;
            state.user = action.payload.user;
            state.token = action.payload.token;
        },
        [loginUser.rejected]: (state, action) => {
            state.status = action.payload?.message || 'Login failed';
            state.isLoading = false;
        },
        // Check authorization
        [getMe.pending]: (state) => {
            state.isLoading = true;
            state.status = null;
        },
        [getMe.fulfilled]: (state, action) => {
            state.isLoading = false;
            state.status = null;
            state.user = action.payload?.user;
            state.token = action.payload?.token;
        },
        [getMe.rejected]: (state, action) => {
            state.status = action.payload?.message || 'Authorization check failed';
            state.isLoading = false;
        },
    },
});

export const checkIsAuth = (state) => Boolean(state.auth.token);

export const { logout } = authSlice.actions;
export default authSlice.reducer;
