import React, { useState, useEffect } from 'react';
import { Route, Switch, NavLink, BrowserRouter as Router } from 'react-router-dom';
import { FaBlog, FaUser, FaCog } from 'react-icons/fa';

// Posts Component
const Posts = ({ posts }) => {
  return (
    <div>
      <h2 className="text-xl font-bold">Посты</h2>
      <ul>
        {posts.map(post => (
          <li key={post.id} className="py-2">
            <h3>{post.title}</h3>
            <p>Автор: {post.author}</p>
            <p>Дата: {post.createdAt}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Users Component
const Users = ({ users }) => {
  return (
    <div>
      <h2 className="text-xl font-bold">Пользователи</h2>
      <ul>
        {users.map(user => (
          <li key={user.id} className="py-2">
            <p>{user.name}</p>
            <p>Роль: {user.role}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export const DashBoard = () => {
  const [posts, setPosts] = useState([]);
  const [users, setUsers] = useState([]);

  // Функция для загрузки постов и пользователей
  useEffect(() => {
    setPosts([
      { id: 1, title: 'Первый пост', author: 'Автор 1', createdAt: '2024-11-01' },
      { id: 2, title: 'Второй пост', author: 'Автор 2', createdAt: '2024-11-02' },
    ]);
    setUsers([
      { id: 1, name: 'Пользователь 1', role: 'Админ' },
      { id: 2, name: 'Пользователь 2', role: 'Редактор' },
    ]);
  }, []);

  return (
    <Router>
      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 bg-gray-200 p-4">
          <nav>
            <ul>
              <li>
                <NavLink to="/dashboard/posts" activeClassName="text-blue-500" className="flex items-center space-x-2 py-2">
                  <FaBlog /> <span>Посты</span>
                </NavLink>
              </li>
              <li>
                <NavLink to="/dashboard/users" activeClassName="text-blue-500" className="flex items-center space-x-2 py-2">
                  <FaUser /> <span>Пользователи</span>
                </NavLink>
              </li>
              <li>
                <NavLink to="#" activeClassName="text-blue-500" className="flex items-center space-x-2 py-2">
                  <FaCog /> <span>Настройки</span>
                </NavLink>
              </li>
            </ul>
          </nav>
        </div>

        {/* Main Content */}
        <div className="flex-1 p-4">
          <Switch>
            <Route path="/dashboard/posts">
              <Posts posts={posts} />
            </Route>
            <Route path="/dashboard/users">
              <Users users={users} />
            </Route>
            {/* Default to Posts if no route matches */}
            <Route path="/" exact>
              <Posts posts={posts} />
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
};

export default DashBoard;
