export const PostPage = () => {
    const [post, setPost] = useState(null)
    const [comment, setComment] = useState('')
    const { user } = useSelector((state) => state.auth)
    const { comments } = useSelector((state) => state.comment)
    const navigate = useNavigate()
    const params = useParams()
    const dispatch = useDispatch()

    const removePostHandler = async () => {
        try {
            await dispatch(removePost(params.id))
            toast('Пост был удален')
            navigate('/posts')
        } catch (error) {
            console.log(error)
            toast.error('Ошибка при удалении поста')
        }
    }

    const handleSubmit = async () => {
        try {
            const postId = params.id
            if (comment.trim()) {
                await dispatch(createComment({ postId, comment }))
                setComment('')
            } else {
                toast.error('Комментарий не может быть пустым!')
            }
        } catch (error) {
            console.log(error)
            toast.error('Ошибка при отправке комментария')
        }
    }

    const fetchComments = useCallback(async () => {
        try {
            await dispatch(getPostComments(params.id))
        } catch (error) {
            console.log(error)
            toast.error('Ошибка при загрузке комментариев')
        }
    }, [params.id, dispatch])

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setPost(data)
    }, [params.id])

    useEffect(() => {
        fetchPost()
    }, [fetchPost])

    useEffect(() => {
        fetchComments()
    }, [fetchComments])

    if (!post) {
        return <div className='text-xl text-center text-white py-10'>Загрузка...</div>
    }

    return (
        <div>
            <button className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4'>
                <Link to='/posts'>Назад</Link>
            </button>

            <div className='flex gap-10 py-8'>
                <div className='w-2/3'>
                    <div className='flex flex-col basis-1/4 flex-grow'>
                        <div className={post?.imgUrl ? 'flex rounded-sm h-80' : 'flex rounded-sm'}>
                            {post?.imgUrl && <img src={`http://localhost:3002/${post.imgUrl}`} alt='img' className='object-cover w-full' />}
                        </div>
                    </div>

                    <div className='flex justify-between items-center pt-2'>
                        <div className='text-xs text-white opacity-50'>{post.username}</div>
                        <div className='text-xs text-white opacity-50'>
                            <Moment date={post.createdAt} format='D MMM YYYY' />
                        </div>
                    </div>
                    <div className='text-white text-xl'>{post.title}</div>
                    <p className='text-white opacity-60 text-xs pt-4'>{post.text}</p>

                    <div className='flex gap-3 items-center mt-2 justify-between'>
                        <div className='flex gap-3 mt-4'>
                            <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                                <AiFillEye /> <span>{post.views}</span>
                            </button>
                            <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                                <AiOutlineMessage /> <span>{post.comments?.length || 0}</span>
                            </button>
                        </div>

                        {user?._id === post.author && (
                            <div className='flex gap-3 mt-4'>
                                <Link to={`/${params.id}/edit`} className='flex items-center justify-center gap-2 text-white opacity-50'>
                                    <AiTwotoneEdit />
                                </Link>
                                <button onClick={removePostHandler} className='flex items-center justify-center gap-2 text-white opacity-50'>
                                    <AiFillDelete />
                                </button>
                            </div>
                        )}
                    </div>
                </div>

                <div className='w-1/3 p-8 bg-gray-700 flex flex-col gap-2 rounded-sm'>
                    <form onSubmit={(e) => e.preventDefault()} className='flex gap-2'>
                        <input
                            type='text'
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                            placeholder='Комментарий'
                            className='text-black w-full rounded-sm bg-gray-400 border p-2 text-xs outline-none placeholder:text-gray-700'
                        />
                        <button
                            type='submit'
                            onClick={handleSubmit}
                            className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4'
                            disabled={!user}
                        >
                            Отправить
                        </button>
                    </form>

                    {comments?.map((cmt) => (
                        <CommentItem key={cmt._id} cmt={cmt} />
                    ))}
                </div>
            </div>
        </div>
    )
}
