import React, { useState } from 'react';
import axios from '../utils/axios';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

export const UpdateUser = () => {
    const [newAvatar, setNewAvatar] = useState(null);
    const [newUsername, setNewUsername] = useState('');
    const [isUsernameTaken, setIsUsernameTaken] = useState(false); // Добавим состояние для проверки

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setNewAvatar(file);
        }
    };

    const handleUsernameChange = (e) => {
        setNewUsername(e.target.value);
    };

    // Проверка на наличие пользователя с таким именем
    const checkUsernameAvailability = async () => {
        try {
            const response = await axios.get(`/auth/user/check-username?username=${newUsername}`);
            if (response.data.isTaken) {
                setIsUsernameTaken(true);
            } else {
                setIsUsernameTaken(false);
            }
        } catch (error) {
            setIsUsernameTaken(false);  // Если ошибка, считаем имя доступным
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault(); // Добавим preventDefault для предотвращения стандартного поведения формы

        if (isUsernameTaken) {
            toast.error('Имя пользователя уже занято');
            return; // Если имя занято, не отправляем запрос
        }

        const formData = new FormData();
        if (newAvatar) formData.append('avatar', newAvatar);
        if (newUsername) formData.append('username', newUsername);

        try {
            const { data } = await axios.put('/auth/user/update', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${window.localStorage.getItem('token')}`,
                },
            });

            dispatch({ type: 'auth/updateUser', payload: data.user });
            navigate('/posts'); // Перенаправляем пользователя только если обновление успешно
        } catch (error) {
            if (error.response && error.response.status === 409) {
                toast.error('Имя пользователя уже занято');
            } else {
                toast.error('Ошибка обновления пользователя');
            }
        }
    };

    return (
        <div className="w-full max-w-4xl mx-auto py-12">
            <h2 className="text-3xl font-semibold text-white mb-6">Change Your Avatar or Username</h2>
            <div className="bg-white p-6 rounded-lg shadow-lg">
                <h3 className="text-2xl font-semibold mb-4">Select New Avatar</h3>
                <input
                    type="file"
                    accept="image/*"
                    onChange={handleAvatarChange}
                    className="mb-4"
                />
                {newAvatar && (
                    <div className="mb-4">
                        <img
                            src={URL.createObjectURL(newAvatar)}
                            alt="Preview"
                            className="w-32 h-32 rounded-full object-cover border-2 border-gray-700"
                        />
                    </div>
                )}
                <div className="mb-4">
                    <label htmlFor="username" className="text-xl font-medium">New Username</label>
                    <input
                        id="username"
                        type="text"
                        value={newUsername}
                        onChange={handleUsernameChange}
                        onBlur={checkUsernameAvailability} // Проверка при потере фокуса
                        className="mt-2 p-2 border border-gray-300 rounded-md w-full"
                        placeholder="Enter new username"
                    />
                    {isUsernameTaken && <span className="text-red-500 text-sm">This username is already taken</span>}
                </div>
                <div className="flex justify-between">
                    <button
                        onClick={() => navigate('/posts')}  
                        className="text-gray-500 hover:underline"
                    >
                        Cancel
                    </button>
                    <button
                        onClick={handleSubmit}
                        className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600"
                    >
                        Save Changes
                    </button>
                </div>
            </div>
        </div>
    );
};
