import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PopularPosts } from '../components/PopularPosts';
import { PostItem } from '../components/PostItem';
import { getAllPosts, selectAvailableTags, getPostsByTag } from '../redux/features/post/postSlice';

export const MainPage = () => {
    const dispatch = useDispatch();
    const { posts, popularPosts } = useSelector((state) => state.post);
    const tags = useSelector(selectAvailableTags);
    const [filteredPosts, setFilteredPosts] = useState([]);

    useEffect(() => {
        dispatch(getAllPosts());
    }, [dispatch]);

    const handleTagClick = (tag) => {
        dispatch(getPostsByTag(tag)).then((action) => {
            if (action.payload) {
                setFilteredPosts(action.payload);
            }
        });
    };

    const displayPosts = filteredPosts.length ? filteredPosts : posts;

    if (!posts.length) {
        return (
            <div className='text-xl text-center text-gray-400 py-20'>
                <p>Постов нет.</p>
            </div>
        );
    }

    return (
        <div className='max-w-screen-xl mx-auto px-6 py-12'>
            <div className='flex justify-between gap-12'>
                <div className='flex flex-col gap-12 w-full md:w-3/4'>
                    {displayPosts?.map((post, idx) => (
                        <PostItem key={idx} post={post} />
                    ))}
                </div>

                <div className='hidden md:block w-full md:w-1/4'>
                    <div className='text-sm font-semibold text-gray-500 mb-6'>
                        <span className='uppercase tracking-wider text-white'>Популярное:</span>
                    </div>

                    <div className='bg-gray-800 rounded-lg p-6 shadow-lg'>
                        {popularPosts?.slice(0, 3).map((post, idx) => (
                            <PopularPosts key={idx} post={post} />
                        ))}
                    </div>
                    <div className='mt-12'>
                        <h2 className='text-lg font-semibold text-white mb-4'>Теги</h2>
                        <div className='flex flex-wrap gap-2'>
                            {tags?.map((tag, idx) => (
                                <span
                                    key={idx}
                                    onClick={() => handleTagClick(tag)}
                                    className='px-3 py-1 bg-gray-700 text-gray-300 rounded hover:bg-gray-600 cursor-pointer'
                                >
                                    #{tag}
                                </span>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};
