import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PopularPosts } from '../components/PopularPosts';
import { PostItem } from '../components/PostItem';
import { getAllPosts, selectAvailableTags, getPostsByTag } from '../redux/features/post/postSlice';
import axios from 'axios';

export const MainPage = () => {
    const dispatch = useDispatch();
    const { posts, popularPosts } = useSelector((state) => state.post);
    const tags = useSelector(selectAvailableTags);
    const [activeTag, setActiveTag] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 4;
    const [fetchedPosts, setFetchedPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                // API URL for fetching posts from backend
                const response = await axios.get('/api/posts', {
                    params: {
                        searchQuery,
                        tag: activeTag,
                        page: currentPage,
                        limit: postsPerPage,
                    },
                });
                setFetchedPosts(response.data);  // Assuming your API returns an array of posts
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        fetchPosts();  // Fetch posts when component mounts or when dependencies change
    }, [searchQuery, activeTag, currentPage]);

    const handleTagClick = (tag) => {
        setActiveTag((prevTag) => {
            const newTag = prevTag === tag ? null : tag;
            setCurrentPage(1); // Reset to page 1 when tag changes
            return newTag;
        });
    };

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
        setCurrentPage(1); // Reset to page 1 when search query changes
    };

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = fetchedPosts.slice(indexOfFirstPost, indexOfLastPost);

    const handlePageChange = (page) => {
        if (page > 0 && page <= totalPages) {
            setCurrentPage(page);
        }
    };

    const totalPages = Math.ceil(fetchedPosts.length / postsPerPage);

    return (
        <div className='max-w-screen-xl mx-auto px-6 py-12 bg-gray-900'>
            <div className='flex flex-col md:flex-row-reverse gap-12'>
                <div className='w-full md:w-1/4'>
                    <div className='text-sm font-semibold text-gray-400 mb-6'>
                        <span className='uppercase tracking-wider text-white'>Популярное:</span>
                    </div>
                    <div className='bg-gray-800 rounded-lg p-6 shadow-lg mb-12'>
                        {popularPosts?.slice(0, 3).map((post, index) => (
                            <PopularPosts key={`popularPost-${index}`} post={post} />
                        ))}
                    </div>
                    <div className='mt-12'>
                        <h2 className='text-lg font-semibold text-white mb-4'>Теги</h2>
                        <div className='flex flex-wrap gap-2'>
                            {tags?.length ? (
                                tags.map((tag, index) => (
                                    <span
                                        key={`tag-${index}`}
                                        role="button"
                                        aria-label={`Filter posts by tag: ${tag}`}
                                        onClick={() => handleTagClick(tag)}
                                        className={`px-4 py-2 rounded-lg cursor-pointer transition duration-300 ease-in-out ${
                                            activeTag === tag
                                                ? 'bg-blue-600 text-white'
                                                : 'bg-gray-700 text-gray-300 hover:bg-blue-500 hover:text-white'
                                        }`}
                                    >
                                        #{tag}
                                    </span>
                                ))
                            ) : (
                                <p className="text-gray-400">Тегов пока нет.</p>
                            )}
                        </div>
                    </div>
                </div>

                <div className='w-full md:w-3/4 flex flex-col gap-5'>
                    <div className='flex justify-between items-center'>
                        <input
                            type="text"
                            value={searchQuery}
                            onChange={handleSearchChange}
                            placeholder="Поиск по названию..."
                            className="p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 w-full"
                        />
                    </div>

                    {currentPosts?.length ? (
                        currentPosts.map((post, index) => (
                            <PostItem key={`post-${index}`} post={post} />
                        ))
                    ) : (
                        <div className="text-xl text-center text-gray-400 py-20">
                            <p>Постов нет.</p>
                        </div>
                    )}

                    {fetchedPosts?.length > postsPerPage && (
                        <div className='flex justify-center mt-6'>
                            <button
                                onClick={() => handlePageChange(currentPage - 1)}
                                disabled={currentPage === 1}
                                className='px-5 py-2 mx-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600 disabled:bg-gray-500 transition duration-300'
                            >
                                Назад
                            </button>
                            {[...Array(totalPages).keys()].map((page) => (
                                <button
                                    key={page}
                                    onClick={() => handlePageChange(page + 1)}
                                    className={`px-5 py-2 mx-2 rounded-lg transition duration-300 ${
                                        currentPage === page + 1
                                            ? 'bg-blue-600 text-white'
                                            : 'bg-gray-700 text-gray-300 hover:bg-blue-500 hover:text-white'
                                    }`}
                                >
                                    {page + 1}
                                </button>
                            ))}
                            <button
                                onClick={() => handlePageChange(currentPage + 1)}
                                disabled={currentPage === totalPages}
                                className='px-5 py-2 mx-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600 disabled:bg-gray-500 transition duration-300'
                            >
                                Вперед
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};
