import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PopularPosts } from '../components/PopularPosts';
import { PostItem } from '../components/PostItem';
import { getAllPosts, selectAvailableTags, getPostsByTag } from '../redux/features/post/postSlice';

export const MainPage = () => {
    const dispatch = useDispatch();
    const { posts, popularPosts } = useSelector((state) => state.post);
    const tags = useSelector(selectAvailableTags);
    const [activeTag, setActiveTag] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 4;

    useEffect(() => {
        if (activeTag) {
            dispatch(getPostsByTag(activeTag));
        } else {
            dispatch(getAllPosts({ searchQuery }));
        }
    }, [dispatch, activeTag, searchQuery]);

    const handleTagClick = (tag) => {
        if (activeTag === tag) {
            setActiveTag(null);
            dispatch(getAllPosts({ searchQuery }));
        } else {
            setActiveTag(tag);
            dispatch(getPostsByTag(tag));
        }
    };

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = posts?.slice(indexOfFirstPost, indexOfLastPost);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    const totalPages = Math.ceil(posts?.length / postsPerPage);

    return (
        <div className='max-w-screen-xl mx-auto px-6 py-12'>
            <div className='flex flex-col md:flex-row-reverse gap-12'>
                <div className='w-full md:w-1/4'>
                    <div className='text-sm font-semibold text-gray-500 mb-6'>
                        <span className='uppercase tracking-wider text-white'>Популярное:</span>
                    </div>
                    <div className='bg-gray-800 rounded-lg p-6 shadow-lg'>
                    {popularPosts?.slice(0, 3).map((post, index) => (
                        <PopularPosts key={`popularPost-${index}`} post={post} />
                    ))}

                    </div>

                    <div className='mt-12'>
                        <h2 className='text-lg font-semibold text-white mb-4'>Теги</h2>
                        <div className='flex flex-wrap gap-2'>
                        {tags?.length ? (
                            tags.map((tag, index) => (
                                <span
                                    key={`tag-${index}`}
                                    role="button"
                                    aria-label={`Filter posts by tag: ${tag}`}
                                    onClick={() => handleTagClick(tag)}
                                    className={`px-3 py-1 rounded cursor-pointer ${
                                        activeTag === tag
                                            ? 'bg-blue-600 text-white'
                                            : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                                    }`}
                                >
                                    #{tag}
                                </span>
                            ))
                        ) : (
                            <p className="text-gray-400">Тегов пока нет.</p>
                        )}

                        </div>
                    </div>
                </div>

                <div className='w-full md:w-3/4 flex flex-col gap-5'>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={handleSearchChange}
                        placeholder="Поиск по названию..."
                        className="mb-6 p-2 rounded bg-gray-700 text-white"
                    />
                    {currentPosts?.length ? (
                        currentPosts.map((post, index) => (
                            <PostItem key={`post-${index}`} post={post} />
                        ))
                    ) : (
                        <div className="text-xl text-center text-gray-400 py-20">
                            <p>Постов нет.</p>
                        </div>
                    )}


                    {/* Pagination */}
                    {posts?.length > postsPerPage && (
                        <div className='flex justify-center mt-6'>
                            <button
                                onClick={() => handlePageChange(currentPage - 1)}
                                disabled={currentPage === 1}
                                className='px-4 py-2 mx-2 bg-gray-700 text-white rounded hover:bg-gray-600 disabled:bg-gray-500'
                            >
                                Назад
                            </button>
                            {[...Array(totalPages).keys()].map((page) => (
                                <button
                                    key={page}
                                    onClick={() => handlePageChange(page + 1)}
                                    className={`px-4 py-2 mx-2 rounded ${currentPage === page + 1 ? 'bg-blue-600 text-white' : 'bg-gray-700 text-gray-300 hover:bg-gray-600'}`}
                                >
                                    {page + 1}
                                </button>
                            ))}
                            <button
                                onClick={() => handlePageChange(currentPage + 1)}
                                disabled={currentPage === totalPages}
                                className='px-4 py-2 mx-2 bg-gray-700 text-white rounded hover:bg-gray-600 disabled:bg-gray-500'
                            >
                                Вперед
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};
