import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { createPost, selectAvailableTags } from '../redux/features/post/postSlice'

export const AddPostPage = () => {
    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    const [image, setImage] = useState('')
    const [selectedTags, setSelectedTags] = useState([])

    const availableTags = useSelector(selectAvailableTags)

    const dispatch = useDispatch()
    const navigate = useNavigate()

    const handleTagChange = (tag) => {
        if (selectedTags.includes(tag)) {
            setSelectedTags(selectedTags.filter(t => t !== tag));
        } else {
            setSelectedTags([...selectedTags, tag]);
        }
    };

    const submitHandler = async (e) => {
        e.preventDefault()
        try {
            const data = new FormData()
            data.append('title', title)
            data.append('text', text)
            data.append('image', image)
            data.append('tags', selectedTags.join(','))

            // Dispatch createPost action
            await dispatch(createPost(data))

            // Optionally, you can add the page and search query params if needed
            const searchQuery = '' // Replace with actual search query if necessary
            const page = 1 // Replace with the actual page number if necessary
            dispatch(getAllPosts({ searchQuery, page }))

            // Navigate to the homepage or wherever you need
            navigate('/')
        } catch (error) {
            console.log(error)
        }
    }

    const clearFormHandler = () => {
        setText('')
        setTitle('')
        setImage('')
        setSelectedTags([])
    }

    const isVideo = (url) => {
        const videoExtensions = ['.mp4', '.webm', '.ogg']
        return videoExtensions.some((ext) => url.endsWith(ext))
    }

    const isImage = (url) => {
        const imageExtensions = ['.jpg', '.jpeg', '.png', '.gif', '.bmp', '.svg']
        return imageExtensions.some((ext) => url.endsWith(ext))
    }

    const handleFileChange = (e) => {
        const file = e.target.files[0]
        if (file) {
            const fileName = file.name.toLowerCase()
            if (isImage(fileName)) {
                setImage(file)
            } else if (isVideo(fileName)) {
                setImage(file)
            } else {
                alert('Please upload an image or video file.')
                setImage('')
            }
        }
    }

    return (
        <form
            className='max-w-lg mx-auto py-12 px-8 bg-gray-800 rounded-lg shadow-xl'
            onSubmit={submitHandler}
        >
            <label className='block text-sm text-gray-300 font-semibold mb-2'>
                Прикрепить файл:
                <div className='relative'>
                    <input
                        type='file'
                        className='hidden'
                        onChange={handleFileChange}
                    />
                    <div className='flex items-center justify-center py-8 bg-gray-700 border-2 border-dotted border-gray-500 rounded-lg cursor-pointer hover:bg-gray-600 transition'>
                        <span className='text-gray-400 text-lg'>
                            Нажмите, чтобы загрузить файл
                        </span>
                    </div>
                </div>
            </label>

            <div className='flex justify-center py-2'>
                {image && (
                    isVideo(image.name) ? (
                        <video
                            width="100%"
                            height="auto"
                            controls
                            className='w-32 h-32 object-cover rounded-lg shadow-md'
                        >
                            <source src={URL.createObjectURL(image)} type="video/mp4" />
                            Ваш браузер не поддерживает видео.
                        </video>
                    ) : (
                        <img
                            src={URL.createObjectURL(image)}
                            alt={image.name}
                            className='w-32 h-32 object-cover rounded-lg shadow-md'
                        />
                    )
                )}
            </div>

            <label className='block text-xs text-gray-200 opacity-80 mt-4'>
                Заголовок поста:
                <input
                    type='text'
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder='Введите заголовок'
                    className='mt-2 text-black w-full rounded-lg bg-gray-400 py-2 px-4 text-sm outline-none focus:ring-2 focus:ring-blue-500 placeholder:text-gray-700 transition'
                />
            </label>

            <label className='block text-xs text-gray-200 opacity-80 mt-4'>
                Теги:
                <div className='flex flex-wrap gap-3 mt-2'>
                    {availableTags.map((tag) => (
                        <button
                            key={tag}
                            type='button'
                            onClick={() => handleTagChange(tag)}
                            className={`px-4 py-2 text-sm font-medium rounded-lg transition-all ${selectedTags.includes(tag) ? 'bg-blue-500 text-white' : 'bg-gray-600 text-gray-200 hover:bg-blue-600'}`}
                        >
                            {tag}
                        </button>
                    ))}
                </div>
            </label>

            <label className='block text-xs text-gray-200 opacity-80 mt-4'>
                Содержание:
                <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder='Введите текст поста'
                    className='mt-2 text-black w-full rounded-lg bg-gray-400 py-2 px-4 text-sm outline-none focus:ring-2 focus:ring-blue-500 placeholder:text-gray-700 transition'
                />
            </label>

            <div className='flex justify-between mt-6'>
                <button
                    type='button'
                    onClick={clearFormHandler}
                    className='px-5 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition'
                >
                    Очистить
                </button>
                <button
                    type='submit'
                    className='px-5 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition'
                >
                    Создать пост
                </button>
            </div>
        </form>
    )
}
