import React, { useState, useEffect, useRef } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { checkIsAuth, logout } from '../redux/features/auth/authSlice';
import { toast } from 'react-toastify';
import { FaBars, FaSignOutAlt, FaMoon, FaSun } from 'react-icons/fa';

export const Navbar = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const isAuth = useSelector(checkIsAuth);
    const [isMobile, setIsMobile] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isDarkMode, setIsDarkMode] = useState(false); // Dark mode state
    const menuRef = useRef(null);
    const { user } = useSelector((state) => state.auth); 

    const handleResize = () => {
        setIsMobile(window.innerWidth <= 720);
    };

    const toggleTheme = () => {
        setIsDarkMode((prevMode) => !prevMode);
    };

    useEffect(() => {
        handleResize(); 
        window.addEventListener('resize', handleResize);

        // Check saved theme in localStorage
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme === 'dark') {
            setIsDarkMode(true);
        } else {
            setIsDarkMode(false);
        }

        // Set the theme class on the body
        if (isDarkMode) {
            document.body.classList.add('dark');
            localStorage.setItem('theme', 'dark');
        } else {
            document.body.classList.remove('dark');
            localStorage.setItem('theme', 'light');
        }

        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setIsMenuOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            window.removeEventListener('resize', handleResize);
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [isDarkMode]);

    const activeStyles = {
        color: isMenuOpen ? 'black' : 'white',
    };

    const logoutHandler = () => {
        dispatch(logout());
        window.localStorage.removeItem('token');
        toast('Вы вышли из системы');
        setIsMenuOpen(false);  
        navigate('/'); 
    };

    const handleLinkClick = () => {
        setIsMenuOpen(false);
    };

    const avatarImg = "/img/User-avatar.png";

    return (
        <div className='header sticky top-0 z-10 flex py-4 justify-between items-center w-full'>
            <span className="flex justify-center items-center w-20 h-8 bg-gradient-to-r from-indigo-600 to-purple-600 text-xs text-white font-semibold rounded-lg shadow-lg hover:scale-105 transform transition duration-300">
                <NavLink to="/" className="flex items-center space-x-2">
                    <span>Blog Market</span>
                </NavLink>
            </span>

            {isAuth && !isMobile && (
                <ul className='flex gap-8'>
                    <li>
                        <NavLink
                            to={'/'}
                            className='text-xs text-gray-400 hover:text-white'
                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                        >
                            Главная
                        </NavLink>
                    </li>
                    <li>
                        <NavLink
                            to={'/new'}
                            className='text-xs text-gray-400 hover:text-white'
                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                        >
                            Добавить пост
                        </NavLink>
                    </li>
                </ul>
            )}

            <div className="flex items-center gap-4">
                <button 
                    onClick={toggleTheme} 
                    className="text-white"
                >
                    {isDarkMode ? <FaSun /> : <FaMoon />}
                </button>

                {isMobile ? (
                    <button
                        className="text-2xl text-white"
                        onClick={() => setIsMenuOpen(!isMenuOpen)}
                    >
                        <FaBars />
                    </button>
                ) : (
                    <div>
                        {isAuth ? (
                            <div className="flex gap-x-4 items-center">
                                <Link
                                    to={'/posts'}
                                    className='border border-solid border-white text-white rounded-full'
                                    onClick={handleLinkClick}
                                >
                                    <img 
                                        src={user?.imgUrl ? `http://localhost:3002/${user.imgUrl}` : avatarImg} 
                                        className="w-10 h-10 rounded-full object-cover"
                                    />
                                </Link>
                                <button
                                    onClick={logoutHandler}
                                    className="flex items-center justify-center w-32 py-2 px-4 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 transition duration-300 ease-in-out"
                                >
                                    <FaSignOutAlt className="mr-2" /> Выйти
                                </button>
                            </div>
                        ) : (
                            <>
                                <Link
                                    to={'/login'}
                                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-3 rounded transition duration-200 mx-2"
                                >
                                    Войти
                                </Link>
                                <Link
                                    to={'/register'}
                                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-3 rounded transition duration-200 mx-2"
                                >
                                    Регистрация
                                </Link>
                            </>
                        )}
                    </div>
                )}

                {isMobile && isMenuOpen && (
                    <div className="absolute top-32 left-0 right-0 bottom-0 bg-gray-800 bg-opacity-75 flex justify-center items-center">
                        <div ref={menuRef} className="bg-gray-100 text-white p-5 rounded-lg w-80">
                            {isAuth && (
                                <ul className='flex flex-col gap-4 items-center'>
                                    <li>
                                        <NavLink
                                            to={'/'}
                                            className='text-center text-xs text-gray-400 hover:text-black'
                                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                                            onClick={handleLinkClick} 
                                        >
                                            Главная
                                        </NavLink>
                                    </li>
                                    <li>
                                        <NavLink
                                            to={'/new'}
                                            className='text-center text-xs text-gray-400 hover:text-black'
                                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                                            onClick={handleLinkClick}
                                        >
                                            Добавить пост
                                        </NavLink>
                                    </li>
                                </ul>
                            )}

                            <div className="text-center">
                                {isAuth ? (
                                    <div className='flex flex-col items-center'>
                                        <Link
                                            to={'/posts'}
                                            className='mt-3'
                                            onClick={handleLinkClick}
                                        >
                                            <img 
                                                src={user?.imgUrl ? `http://localhost:3002/${user.imgUrl}` : avatarImg} 
                                                className="w-10 h-10 rounded-full object-cover"
                                            />
                                        </Link>
                                        <button
                                            onClick={logoutHandler}
                                            className="flex items-center mt-6 justify-center w-full py-2 px-4 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 transition duration-300 ease-in-out"
                                        >
                                            <FaSignOutAlt className="mr-2" /> Выйти
                                        </button>
                                    </div>
                                ) : (
                                    <>
                                        <Link
                                            to={'/login'}
                                            className="block py-1 px-1 mb-2 bg-blue-500 hover:bg-blue-700 rounded"
                                            onClick={handleLinkClick} 
                                        >
                                            Войти
                                        </Link>
                                        <Link
                                            to={'/register'}
                                            className="block py-1 px-1 bg-green-500 hover:bg-green-700 rounded"
                                            onClick={handleLinkClick}
                                        >
                                            Регистрация
                                        </Link>
                                    </>
                                )}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};
