import React from 'react'
import { Link } from 'react-router-dom'

export const PopularPosts = ({ post }) => {
    return (
        <div className='bg-gray-700 my-2 p-4 rounded-lg shadow-lg transition duration-300 transform hover:scale-105'>
            <Link
                to={`${post._id}`}
                className='block text-lg font-semibold text-gray-300 hover:text-white transition duration-200'
            >
                {post.title}
            </Link>
            <div className='mt-2 text-sm text-gray-400'>
                {post.excerpt ? post.excerpt : 'No excerpt available'}
            </div>
        </div>
    )
}
