import React, { useEffect, useState } from 'react';
import axios from '../utils/axios';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getFavorites } from '../redux/features/auth/authSlice';
import { FaPen } from 'react-icons/fa';

export const PostsPage = () => {
    const [posts, setPosts] = useState([]);
    const [favorites, setFavorites] = useState([]);

    const { user, token } = useSelector((state) => state.auth);
    const dispatch = useDispatch();

    const fetchMyPosts = async () => {
        try {
            const { data } = await axios.get('/posts/user/me');
            setPosts(data);
        } catch (error) {
            console.log(error);
        }
    };

    const fetchFavorites = async () => {
        try {
            const { data } = await axios.get('/auth/favorites', { // Change this line
                headers: { Authorization: `Bearer ${token}` },
            });
            setFavorites(data.favorites);
        } catch (error) {
            console.log(error);
        }
    };    

    const avatarImg = "/img/User-avatar.png";

    const isVideo = (url) => {
        if (url && typeof url === 'string') {
          return (
            url.endsWith('.mp4') ||
            url.endsWith('.avi') ||
            url.endsWith('.mov') ||
            url.endsWith('.wmv') ||
            url.endsWith('.mkv') ||
            url.endsWith('.webm') ||
            url.endsWith('.flv') ||
            url.endsWith('.ogg')
          );
        }
        return false; 
    };

    useEffect(() => {
        fetchMyPosts();
        fetchFavorites();
    }, [dispatch, token]);

    return (
        <div className="w-full max-w-6xl mx-auto py-12 px-4 sm:px-6 lg:px-8">
            <div className="flex items-center mb-8 space-x-4">
                <Link to="/update_user" className="relative group">
                    <img
                        src={user?.imgUrl ? `http://localhost:3002/${user.imgUrl}` : avatarImg}
                        alt="User Avatar"
                        className="w-24 h-24 rounded-full object-cover border-4 border-gray-700 cursor-pointer transform transition duration-300 ease-in-out hover:scale-105 hover:opacity-90"
                    />
                    <FaPen className="absolute inset-0 m-auto text-white opacity-0 group-hover:opacity-100 transition-all duration-300" size={20} />
                </Link>
                <div>
                    <h2 className="text-4xl font-semibold text-white">{user?.username || "User"}</h2>
                    <p className="text-gray-300 text-sm">Welcome back!</p>
                </div>
            </div>

            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 mb-12">
                {posts?.length === 0 ? (
                    <p className="text-white col-span-full text-center">У вас нет постов.</p>
                ) : (
                    posts?.filter(post => post !== null && post.imgUrl).map((post, idx) => (
                        <div key={idx} className="relative h-48 w-full rounded-lg overflow-hidden border-2 border-gray-600 shadow-lg hover:shadow-xl transition-shadow duration-300">
                            {isVideo(post.imgUrl) ? (
                                <Link to={`/${post._id}`} className="block w-full h-full">
                                    <video
                                        width="100%"
                                        height="100%"
                                        className="object-cover w-full h-full"
                                        muted
                                        loop
                                    >
                                        <source src={`http://localhost:3002/${post.imgUrl}`} type={`video/${post.imgUrl.split('.').pop()}`} />
                                        Ваш браузер не поддерживает видео.
                                    </video>
                                </Link>
                            ) : (
                                <Link to={`/${post._id}`} className="block w-full h-full">
                                    <img
                                        src={`http://localhost:3002/${post.imgUrl}`}
                                        alt="Post content"
                                        className="object-cover w-full h-full"
                                    />
                                </Link>
                            )}
                        </div>
                    ))
                )}
            </div>

            <div className="max-w-screen-xl mx-auto p-6 rounded-lg bg-gray-800">
                <h2 className="text-3xl font-semibold text-white mb-4">Избранные Посты</h2>
                {favorites?.length === 0 ? (
                    <p className="text-white text-center">У вас нет избранных постов.</p>
                ) : (
                    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6">
                        {favorites?.map((post, idx) => (
                            <div key={idx} className="relative h-48 w-full rounded-lg overflow-hidden border-2 border-gray-600 shadow-lg hover:shadow-xl transition-shadow duration-300">
                                {isVideo(post.imgUrl) ? (
                                    post.imgUrl ? (
                                        <Link to={`/${post._id}`} className="block w-full h-full">
                                            <video
                                                width="100%"
                                                height="100%"
                                                className="object-cover w-full h-full"
                                                muted
                                                loop
                                            >
                                                <source src={`http://localhost:3002/${post.imgUrl}`} type={`video/${post.imgUrl.split('.').pop()}`} />
                                                Ваш браузер не поддерживает видео.
                                            </video>
                                        </Link>
                                    ) : (
                                        <p className="text-center text-white">Нет видео</p>
                                    )
                                ) : (
                                    post.imgUrl ? (
                                        <Link to={`/${post._id}`} className="block w-full h-full">
                                            <img
                                                src={`http://localhost:3002/${post.imgUrl}`}
                                                alt="Post content"
                                                className="object-cover w-full h-full"
                                            />
                                        </Link>
                                    ) : (
                                        <p className="text-center text-white">Изображение не доступно</p>
                                    )
                                )}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );

};
