import React, { useState } from 'react';
import axios from '../utils/axios';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';

export const ChangeAvatar = () => {
    const [newAvatar, setNewAvatar] = useState(null);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setNewAvatar(file);
        }
    };

    const handleAvatarSubmit = async () => {
        if (!newAvatar) {
            alert("Please select an avatar image.");
            return;
        }

        const formData = new FormData();
        formData.append('avatar', newAvatar);

        try {
            const { data } = await axios.post('http://localhost:3002/api/auth/upload-avatar', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${window.localStorage.getItem('token')}`,
                },
            });

            // Update the avatar in the Redux store immediately after upload
            dispatch({ type: 'auth/updateAvatar', payload: data.imgUrl });

            // No need to navigate, avatar is updated in the store immediately
        } catch (error) {
            console.error('Error uploading avatar:', error);
            alert('Failed to upload avatar');
        }
    };

    return (
        <div className="w-full max-w-4xl mx-auto py-12">
            <h2 className="text-3xl font-semibold text-white mb-6">Change Your Avatar</h2>
            <div className="bg-white p-6 rounded-lg shadow-lg">
                <h3 className="text-2xl font-semibold mb-4">Select New Avatar</h3>
                <input
                    type="file"
                    accept="image/*"
                    onChange={handleAvatarChange}
                    className="mb-4"
                />
                {newAvatar && (
                    <div className="mb-4">
                        <img
                            src={URL.createObjectURL(newAvatar)}
                            alt="Preview"
                            className="w-32 h-32 rounded-full object-cover border-2 border-gray-700"
                        />
                    </div>
                )}
                <div className="flex justify-between">
                    <button
                        onClick={() => navigate('/posts')}
                        className="text-gray-500 hover:underline"
                    >
                        Cancel
                    </button>
                    <button
                        onClick={handleAvatarSubmit}
                        className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600"
                    >
                        Save Avatar
                    </button>
                </div>
            </div>
        </div>
    );
};
