import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { PopularPosts } from '../components/PopularPosts';
import { PostItem } from '../components/PostItem';
import { getAllPosts, selectAvailableTags, getPostsByTag } from '../redux/features/post/postSlice';

export const MainPage = () => {
    const dispatch = useDispatch();
    const { posts, popularPosts, totalPostsCount } = useSelector((state) => state.post);
    const tags = useSelector(selectAvailableTags);
    const [activeTag, setActiveTag] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [loading, setLoading] = useState(false);  // Состояние загрузки
    const postsPerPage = 4;

    const handleTagClick = (tag) => {
        setActiveTag((prevTag) => {
            const newTag = prevTag === tag ? null : tag;
            setCurrentPage(1);  // Сброс текущей страницы при изменении тега
            setLoading(true);  // Устанавливаем состояние загрузки
            if (newTag) {
                dispatch(getPostsByTag({ tag: newTag, searchQuery, page: 1 }))
                    .finally(() => setLoading(false));  // Завершаем загрузку
            } else {
                dispatch(getAllPosts({ searchQuery, page: 1 }))
                    .finally(() => setLoading(false));  // Завершаем загрузку
            }
            return newTag;
        });
    };

    const handleSearchChange = (event) => {
        const query = event.target.value;
        setSearchQuery(query);
        setCurrentPage(1);  // Сброс страницы на 1 при изменении поискового запроса
        setLoading(true);  // Устанавливаем состояние загрузки
        if (activeTag) {
            dispatch(getPostsByTag({ tag: activeTag, searchQuery: query, page: 1 }))
                .finally(() => setLoading(false));  // Завершаем загрузку
        } else {
            dispatch(getAllPosts({ searchQuery: query, page: 1 }))
                .finally(() => setLoading(false));  // Завершаем загрузку
        }
    };

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = posts?.slice(indexOfFirstPost, indexOfLastPost);
    const totalPages = Math.ceil(totalPostsCount / postsPerPage);

    const handlePageChange = (page) => {
        setCurrentPage(page);
        setLoading(true);  // Устанавливаем состояние загрузки
        if (activeTag) {
            dispatch(getPostsByTag({ tag: activeTag, searchQuery, page }))
                .finally(() => setLoading(false));  // Завершаем загрузку
        } else {
            dispatch(getAllPosts({ searchQuery, page }))
                .finally(() => setLoading(false));  // Завершаем загрузку
        }
    };

    useEffect(() => {
        console.log(`Fetching posts for page ${currentPage}`);
        setLoading(true);  // Устанавливаем состояние загрузки
        if (activeTag) {
            dispatch(getPostsByTag({ tag: activeTag, searchQuery, page: currentPage }))
                .finally(() => setLoading(false));  // Завершаем загрузку
        } else {
            dispatch(getAllPosts({ searchQuery, page: currentPage }))
                .finally(() => setLoading(false));  // Завершаем загрузку
        }
    }, [dispatch, activeTag, searchQuery, currentPage]);

    return (
        <div className='max-w-screen-xl mx-auto px-6 py-12 bg-gray-900'>
            {loading ? (
                <div className="text-center text-gray-400 py-20">Загрузка...</div>
            ) : (
                <div className='flex flex-col md:flex-row-reverse gap-12'>
                    {/* Блок популярных постов */}
                    <div className='w-full md:w-1/4'>
                        <div className='text-sm font-semibold text-gray-400 mb-6'>
                            <span className='uppercase tracking-wider text-white'>Популярное:</span>
                        </div>
                        <div className='bg-gray-800 rounded-lg p-6 shadow-lg mb-12'>
                            {popularPosts?.slice(0, 3).map((post, index) => (
                                <PopularPosts key={`popularPost-${index}`} post={post} />
                            ))}
                        </div>

                        {/* Теги */}
                        <div className='mt-12'>
                            <h2 className='text-lg font-semibold text-white mb-4'>Теги</h2>
                            <div className='flex flex-wrap gap-2'>
                                {tags?.length ? (
                                    tags.map((tag, index) => (
                                        <span
                                            key={`tag-${index}`}
                                            role="button"
                                            aria-label={`Filter posts by tag: ${tag}`}
                                            onClick={() => handleTagClick(tag)}
                                            className={`px-4 py-2 rounded-lg cursor-pointer transition duration-300 ease-in-out ${
                                                activeTag === tag
                                                    ? 'bg-blue-600 text-white'
                                                    : 'bg-gray-700 text-gray-300 hover:bg-blue-500 hover:text-white'
                                            }`}
                                        >
                                            #{tag}
                                        </span>
                                    ))
                                ) : (
                                    <p className="text-gray-400">Тегов пока нет.</p>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Посты */}
                    <div className='w-full md:w-3/4 flex flex-col gap-5'>
                        <div className='flex justify-between items-center'>
                            <input
                                type="text"
                                value={searchQuery}
                                onChange={handleSearchChange}
                                placeholder="Поиск по названию..."
                                className="p-3 rounded-lg bg-gray-700 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 w-full"
                            />
                        </div>

                        {/* Рендеринг постов */}
                        {currentPosts && currentPosts.length ? (
                            currentPosts.map((post, index) => (
                                <PostItem key={`post-${index}`} post={post} />
                            ))
                        ) : (
                            <div className="text-xl text-center text-gray-400 py-20">
                                <p>Постов нет.</p>
                            </div>
                        )}

                        {/* Пагинация */}
                        {totalPages > 1 && (
                            <div className='flex justify-center mt-6'>
                                <button
                                    onClick={() => handlePageChange(currentPage - 1)}
                                    disabled={currentPage === 1}
                                    className='px-5 py-2 mx-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600 disabled:bg-gray-500 transition duration-300'
                                >
                                    Назад
                                </button>
                                {[...Array(totalPages).keys()].map((page) => (
                                    <button
                                        key={page}
                                        onClick={() => handlePageChange(page + 1)}
                                        className={`px-5 py-2 mx-2 rounded-lg transition duration-300 ${
                                            currentPage === page + 1
                                                ? 'bg-blue-600 text-white'
                                                : 'bg-gray-700 text-gray-300 hover:bg-blue-500 hover:text-white'
                                        }`}
                                    >
                                        {page + 1}
                                    </button>
                                ))}
                                <button
                                    onClick={() => handlePageChange(currentPage + 1)}
                                    disabled={currentPage === totalPages}
                                    className='px-5 py-2 mx-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600 disabled:bg-gray-500 transition duration-300'
                                >
                                    Вперед
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};
