import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { checkIsAuth, logout } from '../redux/features/auth/authSlice';
import { toast } from 'react-toastify';
import { FaBars } from 'react-icons/fa';

export const Navbar = () => {
    const isAuth = useSelector(checkIsAuth);
    const dispatch = useDispatch();
    const [isMobile, setIsMobile] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const handleResize = () => {
        setIsMobile(window.innerWidth <= 768);
    };

    useEffect(() => {
        handleResize(); // Check initial screen size
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const activeStyles = {
        color: 'white',
    };

    const logoutHandler = () => {
        dispatch(logout());
        window.localStorage.removeItem('token');
        toast('Вы вышли из системы');
    };

    return (
        <div className='flex py-4 justify-between items-center'>
            <span className='flex justify-center items-center w-20 h-7 bg-gray-600 text-xs text-white rounded-sm'>
                <NavLink to={'/'}>Blog Market</NavLink>
            </span>

            {isAuth && (
                <ul className='flex gap-8'>
                    <li>
                        <NavLink
                            to={'/'}
                            className='text-xs text-gray-400 hover:text-white'
                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                        >
                            Главная
                        </NavLink>
                    </li>
                    <li>
                        <NavLink
                            to={'/posts'}
                            className='text-xs text-gray-400 hover:text-white'
                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                        >
                            Мои посты
                        </NavLink>
                    </li>
                    <li>
                        <NavLink
                            to={'/new'}
                            className='text-xs text-gray-400 hover:text-white'
                            style={({ isActive }) => (isActive ? activeStyles : undefined)}
                        >
                            Добавить пост
                        </NavLink>
                    </li>
                </ul>
            )}

            <div>
                {isMobile ? (
                    <button
                        className="text-2xl text-white"
                        onClick={() => setIsMenuOpen(!isMenuOpen)}
                    >
                        <FaBars />
                    </button>
                ) : (
                    <div>
                        {isAuth ? (
                            <button
                                onClick={logoutHandler}
                                className="block text-center text-white py-1 px-1 mb-2 bg-red-500 hover:bg-red-700 rounded"
                            >
                                Выйти
                            </button>
                        ) : (
                            <>
                                <Link
                                    to={'/login'}
                                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-3 rounded transition duration-200 mx-2"
                                >
                                    Войти
                                </Link>
                                <Link
                                    to={'/register'}
                                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-3 rounded transition duration-200 mx-2"
                                >
                                    Регистрация
                                </Link>
                            </>
                        )}
                    </div>
                )}

                {isMobile && isMenuOpen && (
                    <div className="absolute top-0 left-0 right-0 bottom-0 bg-gray-800 bg-opacity-75 flex justify-center items-center">
                        <div className="bg-gray-100 text-white p-5 rounded-lg w-80">
                            <ul className='flex flex-col gap-4'>
                                <li>
                                    <NavLink
                                        to={'/'}
                                        className='text-center text-xs text-gray-400 hover:text-white'
                                        style={({ isActive }) => (isActive ? activeStyles : undefined)}
                                    >
                                        Главная
                                    </NavLink>
                                </li>
                                <li>
                                    <NavLink
                                        to={'/posts'}
                                        className='text-center text-xs text-gray-400 hover:text-white'
                                        style={({ isActive }) => (isActive ? activeStyles : undefined)}
                                    >
                                        Мои посты
                                    </NavLink>
                                </li>
                                <li>
                                    <NavLink
                                        to={'/new'}
                                        className='text-center text-xs text-gray-400 hover:text-white'
                                        style={({ isActive }) => (isActive ? activeStyles : undefined)}
                                    >
                                        Добавить пост
                                    </NavLink>
                                </li>
                            </ul>

                            <div className="mt-8 text-center">
                                {isAuth ? (
                                    <button
                                        onClick={logoutHandler}
                                        className="block text-center py-1 px-1 mb-2 bg-red-500 hover:bg-red-700 rounded"
                                    >
                                        Выйти
                                    </button>
                                ) : (
                                    <>
                                        <Link
                                            to={'/login'}
                                            className="block py-1 px-1 mb-2 bg-blue-500 hover:bg-blue-700 rounded"
                                        >
                                            Войти
                                        </Link>
                                        <Link
                                            to={'/register'}
                                            className="block py-1 px-1 bg-green-500 hover:bg-green-700 rounded"
                                        >
                                            Регистрация
                                        </Link>
                                    </>
                                )}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};
