import React, { useEffect, useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { removePost } from '../redux/features/post/postSlice';
import { createComment, getPostComments } from '../redux/features/comment/commentSlice';
import { PostItem } from '../components/PostItem';
import { CommentItem } from '../components/CommentItem';

export const PostPage = () => {
    const [post, setPost] = useState(null);
    const [comment, setComment] = useState('');
    const { user } = useSelector((state) => state.auth);
    const { comments } = useSelector((state) => state.comment);
    const navigate = useNavigate();
    const params = useParams();
    const dispatch = useDispatch();

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`);
        setPost(data);
    }, [params.id]);

    useEffect(() => {
        fetchPost();
    }, [fetchPost]);

    const removePostHandler = (postId) => {
        try {
            dispatch(removePost(postId));
            toast('Пост был удален');
            navigate('/');
        } catch (error) {
            console.log(error);
        }
    };

    const handleSubmit = () => {
        try {
            const postId = params.id;
            dispatch(createComment({ postId, comment }));
            setComment('');
        } catch (error) {
            console.log(error);
        }
    };

    const fetchComments = useCallback(async () => {
        try {
            dispatch(getPostComments(params.id));
        } catch (error) {
            console.log(error);
        }
    }, [params.id, dispatch]);

    useEffect(() => {
        fetchComments();
    }, [fetchComments]);

    if (!post) {
        return (
            <div className='text-xl text-center text-white py-10'>
                Загрузка...
            </div>
        );
    }

    return (
        <div className='w-full max-w-3xl mx-auto'>
            <PostItem post={post} onRemovePost={removePostHandler} />
            <div className='my-6'>
                <div className='text-2xl font-semibold text-gray-800'>Комментарии</div>
                <div className='mt-4'>
                    <div className='flex flex-col gap-4'>
                        {comments?.map((comment) => (
                            <CommentItem key={comment._id} comment={comment} />
                        ))}
                    </div>
                </div>

                <div className='mt-8'>
                    <textarea
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        className='w-full p-4 border border-gray-300 rounded-lg'
                        rows="4"
                        placeholder="Напишите комментарий..."
                    />
                    <button
                        onClick={handleSubmit}
                        className='mt-2 px-6 py-2 bg-blue-500 text-white rounded-lg'
                    >
                        Отправить
                    </button>
                </div>
            </div>
        </div>
    );
};
