import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { FaUser, FaBlog, FaCog, FaComment, FaSignOutAlt, FaHome } from 'react-icons/fa';
import { useDispatch, useSelector } from 'react-redux';
import { PostItem } from '../components/PostItem';
import { getAllPosts } from '../redux/features/post/postSlice';

const users = [
  { id: 1, name: 'Пользователь 1' },
  { id: 2, name: 'Пользователь 2' },
  { id: 3, name: 'Пользователь 3' },
];

const comments = [
  { id: 1, text: 'Комментарий 1' },
  { id: 2, text: 'Комментарий 2' },
  { id: 3, text: 'Комментарий 3' },
];

export const DashBoard = () => {
    const dispatch = useDispatch();
    const { posts } = useSelector((state) => state.post);
    const [activeSection, setActiveSection] = useState('overview'); // "overview" для основного контента

    const handleNavClick = (section) => {
        setActiveSection(section);
    };

    useEffect(() => {
        dispatch(getAllPosts());
    }, [dispatch]);

    const stats = {
        users: users.length,
        posts: posts.length,
        comments: comments.length,
    };

    const handleLogout = () => {
        // Logic for logging out
        console.log('Logged out');
    };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white p-6 shadow-lg">
        <div 
          className="text-2xl font-semibold mb-8 cursor-pointer"
          onClick={() => handleNavClick('overview')}
        >
          Admin Panel
        </div>
        <nav>
          <ul>
            <li>
              <NavLink 
                to="#" 
                onClick={() => handleNavClick('overview')} 
                activeClassName="text-blue-400"
                className="flex items-center space-x-3 py-2 hover:bg-gray-700 px-3 rounded-md"
              >
                <FaHome /> <span>Главная</span>
              </NavLink>
            </li>
            <li>
              <NavLink 
                to="#" 
                onClick={() => handleNavClick('posts')} 
                activeClassName="text-blue-400"
                className="flex items-center space-x-3 py-2 hover:bg-gray-700 px-3 rounded-md"
              >
                <FaBlog /> <span>Посты</span>
              </NavLink>
            </li>
            <li>
              <NavLink 
                to="#" 
                onClick={() => handleNavClick('users')} 
                activeClassName="text-blue-400"
                className="flex items-center space-x-3 py-2 hover:bg-gray-700 px-3 rounded-md"
              >
                <FaUser /> <span>Пользователи</span>
              </NavLink>
            </li>
            <li>
              <NavLink 
                to="#" 
                onClick={() => handleNavClick('comments')} 
                activeClassName="text-blue-400"
                className="flex items-center space-x-3 py-2 hover:bg-gray-700 px-3 rounded-md"
              >
                <FaComment /> <span>Комментарии</span>
              </NavLink>
            </li>
            <li>
              <NavLink 
                to="#" 
                onClick={() => handleNavClick('settings')} 
                activeClassName="text-blue-400"
                className="flex items-center space-x-3 py-2 hover:bg-gray-700 px-3 rounded-md"
              >
                <FaCog /> <span>Настройки</span>
              </NavLink>
            </li>
            <li>
              <button 
                onClick={handleLogout} 
                className="flex items-center space-x-3 py-2 hover:bg-gray-700 px-3 rounded-md w-full mt-4 text-red-500"
              >
                <FaSignOutAlt /> <span>Выйти</span>
              </button>
            </li>
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 bg-gray-100 p-6">
        {activeSection === 'overview' && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            <div className="bg-white p-6 rounded-md shadow-lg">
              <div className="text-xl font-semibold mb-2">Пользователи</div>
              <div className="text-3xl font-bold text-gray-700">{stats.users}</div>
            </div>
            <div className="bg-white p-6 rounded-md shadow-lg">
              <div className="text-xl font-semibold mb-2">Посты</div>
              <div className="text-3xl font-bold text-gray-700">{stats.posts}</div>
            </div>
            <div className="bg-white p-6 rounded-md shadow-lg">
              <div className="text-xl font-semibold mb-2">Комментарии</div>
              <div className="text-3xl font-bold text-gray-700">{stats.comments}</div>
            </div>
          </div>
        )}

        {activeSection === 'posts' && (
           <div className='flex flex-col w-full md:w-2/5'>
                {posts?.map((post, idx) => (
                    <PostItem key={idx} post={post} />
                ))}
            </div>
        )}

        {activeSection === 'users' && (
          <div>
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Пользователи</h2>
            {users.map((user) => (
              <div key={user.id} className="bg-white p-6 rounded-md shadow-lg mb-4">
                <div className="text-xl font-semibold">{user.name}</div>
              </div>
            ))}
          </div>
        )}

        {activeSection === 'comments' && (
          <div>
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Комментарии</h2>
            {comments.map((comment) => (
              <div key={comment.id} className="bg-white p-6 rounded-md shadow-lg mb-4">
                <p className="text-gray-700">{comment.text}</p>
              </div>
            ))}
          </div>
        )}

        {activeSection === 'settings' && (
          <div className="bg-white p-6 rounded-md shadow-lg">
            <p>Настройки админ-панели.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DashBoard;
